import React, { useState, useEffect, useRef } from 'react';

function ChatBot({ onBack }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isDarkMode, setIsDarkMode] = useState(() => {
    return localStorage.getItem('darkMode') === 'true' || false;
  });
  const [isMinimized, setIsMinimized] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const chatEndRef = useRef(null);

  // Load chat history from localStorage
  useEffect(() => {
    const savedMessages = localStorage.getItem('chatMessages');
    if (savedMessages) {
      setMessages(JSON.parse(savedMessages));
    } else {
      // Welcome message on first load
      setMessages([
        {
          sender: 'Bot',
          text: 'Hello! Welcome to our company chatbot. How can I assist you today?',
          timestamp: new Date().toISOString(),
          quickReplies: ['Product Info', 'Support', 'Hours', 'Contact Us'],
        },
      ]);
    }
  }, []);

  // Save chat history to localStorage and scroll to bottom
  useEffect(() => {
    localStorage.setItem('chatMessages', JSON.stringify(messages));
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Save dark mode preference
  useEffect(() => {
    localStorage.setItem('darkMode', isDarkMode);
  }, [isDarkMode]);

  const handleSend = (userInput = input.trim()) => {
    if (!userInput) {
      alert('Please enter some input');
      return;
    }

    const userMessage = {
      sender: 'You',
      text: userInput,
      timestamp: new Date().toISOString(),
    };
    setMessages((prev) => [...prev, userMessage]);
    setInput('');

    // Simulate typing delay
    setIsTyping(true);
    setTimeout(() => {
      const botResponse = generateBotResponse(userInput.toLowerCase());
      setMessages((prev) => [...prev, botResponse]);
      setIsTyping(false);
    }, 1000); // 1-second delay for typing effect
  };

  const generateBotResponse = (userInput) => {
    const timestamp = new Date().toISOString();
    let text = '';
    let quickReplies = [];

    if (userInput.includes('hello') || userInput.includes('hi')) {
      text = 'Hello! How can I assist you today?';
      quickReplies = ['Product Info', 'Support', 'Hours', 'Contact Us'];
    } else if (userInput.includes('product') || userInput === 'product info') {
      text = 'We offer a range of products including software solutions, AI tools, and more. Would you like to know about a specific product?';
      quickReplies = ['Software Solutions', 'AI Tools', 'Pricing'];
    } else if (userInput.includes('support') || userInput === 'support') {
      text = 'I’m here to help! What issue are you facing? You can also email us at support@company.com.';
      quickReplies = ['Technical Issue', 'Billing', 'Other'];
    } else if (userInput.includes('hours') || userInput === 'hours') {
      text = 'Our business hours are Monday to Friday, 9 AM to 5 PM. We’re closed on weekends.';
    } else if (userInput.includes('contact') || userInput === 'contact us') {
      text = 'You can reach us at support@company.com or call us at (123) 456-7890.';
    } else if (userInput.includes('software') || userInput === 'software solutions') {
      text = 'Our software solutions include CRM systems, project management tools, and more. Would you like a demo?';
      quickReplies = ['Yes, Schedule Demo', 'No, Thanks'];
    } else if (userInput.includes('ai') || userInput === 'ai tools') {
      text = 'Our AI tools help with automation, data analysis, and more. Interested in learning more?';
      quickReplies = ['Yes, Tell Me More', 'No, Thanks'];
    } else if (userInput.includes('pricing') || userInput === 'pricing') {
      text = 'Pricing depends on the product. For example, our CRM starts at $50/month. Would you like a detailed quote?';
      quickReplies = ['Yes, Get Quote', 'No, Thanks'];
    } else if (userInput.includes('technical') || userInput === 'technical issue') {
      text = 'Sorry to hear that! Please describe the issue, or contact our tech team at techsupport@company.com.';
    } else if (userInput.includes('billing') || userInput === 'billing') {
      text = 'For billing inquiries, please email billing@company.com or call (123) 456-7890.';
    } else if (userInput.includes('demo') || userInput === 'yes, schedule demo') {
      text = 'Great! Please email sales@company.com to schedule a demo.';
    } else if (userInput.includes('quote') || userInput === 'yes, get quote') {
      text = 'Please provide your email, and our sales team will send you a detailed quote.';
    } else if (userInput.includes('bye') || userInput.includes('goodbye')) {
      text = 'Thank you for chatting! Have a great day!';
    } else if (userInput.includes('face recognition')) {
      text = 'Face recognition works by detecting a face in an image, extracting unique features from it, and comparing these features with stored data to identify the person.';
    } else if (userInput.includes('who created you')) {
      text = 'I was created by the team at xAI to assist our customers.';
    } else if (userInput.includes('what is your name')) {
      text = 'I’m Grok, your friendly assistant!';
    } else {
      text = 'I’m sorry, I didn’t understand that. Can you try again?';
      quickReplies = ['Product Info', 'Support', 'Hours', 'Contact Us'];
    }

    return { sender: 'Bot', text, timestamp, quickReplies };
  };

  const handleQuickReply = (reply) => {
    handleSend(reply);
  };

  const handleClear = () => {
    setMessages([
      {
        sender: 'Bot',
        text: 'Chat cleared. How can I assist you now?',
        timestamp: new Date().toISOString(),
        quickReplies: ['Product Info', 'Support', 'Hours', 'Contact Us'],
      },
    ]);
    setInput('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  const toggleDarkMode = () => {
    setIsDarkMode((prev) => !prev);
  };

  const formatTimestamp = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  return (
    <div
      className="min-h-screen flex flex-col items-center"
      style={{
        backgroundImage: 'url(/images/bb.jpg)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        backgroundColor: 'rgba(0, 0, 0, 0.3)',
        backgroundBlendMode: 'overlay',
      }}
    >
      {/* Chatbot Container */}
      <div className={`w-full max-w-md ${isMinimized ? 'h-16' : 'h-auto'} transition-all duration-300`}>
        {/* Header */}
        <div className="bg-white shadow-md rounded-t-lg">
          <div className="flex items-center justify-between p-4">
            <div className="flex items-center">
              <img
                src="/images/company-logo.png"
                alt="Company Logo"
                className="w-10 h-10 mr-2 rounded-full"
              />
              <h1 className="text-xl font-bold text-gray-800">Customer Support</h1>
            </div>
            <div className="flex items-center gap-2">
              <button
                onClick={toggleDarkMode}
                className="bg-gray-500 text-white px-3 py-1 rounded text-sm"
              >
                {isDarkMode ? 'Light' : 'Dark'}
              </button>
              <button
                onClick={() => setIsMinimized(!isMinimized)}
                className="bg-gray-500 text-white px-3 py-1 rounded text-sm"
              >
                {isMinimized ? 'Expand' : 'Minimize'}
              </button>
              <button onClick={onBack} className="bg-gray-500 text-white px-3 py-1 rounded text-sm">
                Back
              </button>
            </div>
          </div>
        </div>

        {/* Chat Area (Hidden when minimized) */}
        {!isMinimized && (
          <>
            <div
              className={`w-full p-4 shadow-md flex-grow overflow-y-auto h-96 ${
                isDarkMode ? 'bg-gray-800' : 'bg-white'
              }`}
            >
              {messages.map((msg, index) => (
                <div key={index} className="mb-4">
                  <div
                    className={`flex ${
                      msg.sender === 'You' ? 'justify-end' : 'justify-start'
                    } items-start gap-2`}
                  >
                    {msg.sender === 'Bot' && (
                      <img
                        src="/images/bot-avatar.png"
                        alt="Bot Avatar"
                        className="w-8 h-8 rounded-full"
                      />
                    )}
                    <div>
                      <div
                        className={`inline-block p-3 rounded-lg ${
                          msg.sender === 'You'
                            ? 'bg-blue-500 text-white'
                            : isDarkMode
                            ? 'bg-gray-600 text-white'
                            : 'bg-gray-200 text-black'
                        }`}
                      >
                        <strong>{msg.sender}:</strong> {msg.text}
                      </div>
                      <div
                        className={`text-xs mt-1 ${
                          isDarkMode ? 'text-gray-400' : 'text-gray-600'
                        } ${msg.sender === 'You' ? 'text-right' : 'text-left'}`}
                      >
                        {formatTimestamp(msg.timestamp)}
                      </div>
                      {msg.quickReplies && (
                        <div className="flex flex-wrap gap-2 mt-2">
                          {msg.quickReplies.map((reply, idx) => (
                            <button
                              key={idx}
                              onClick={() => handleQuickReply(reply)}
                              className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm hover:bg-blue-200"
                            >
                              {reply}
                            </button>
                          ))}
                        </div>
                      )}
                    </div>
                    {msg.sender === 'You' && (
                      <img
                        src="/images/user-avatar.png"
                        alt="User Avatar"
                        className="w-8 h-8 rounded-full"
                      />
                    )}
                  </div>
                </div>
              ))}
              {isTyping && (
                <div className="flex items-center gap-2">
                  <img
                    src="/images/bot-avatar.png"
                    alt="Bot Avatar"
                    className="w-8 h-8 rounded-full"
                  />
                  <div className="bg-gray-200 p-3 rounded-lg">
                    <span className="animate-pulse">Typing...</span>
                  </div>
                </div>
              )}
              <div ref={chatEndRef} />
            </div>

            {/* Input Area */}
            <div className="w-full bg-white p-4 shadow-md rounded-b-lg flex items-center gap-2">
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Type your message..."
                className="flex-grow p-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                style={{ fontFamily: 'Arial', fontSize: '14px' }}
              />
              <button
                onClick={() => handleSend()}
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
              >
                Send
              </button>
              <button
                onClick={handleClear}
                className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              >
                Clear
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default ChatBot;
