import React, { useState } from 'react';

function Train({ onBack }) {
  const [isTraining, setIsTraining] = useState(false);
  const [progress, setProgress] = useState(0);

  const trainClassifier = async () => {
    setIsTraining(true);
    setProgress(0);

    try {
      // Simulate progress updates
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev >= 100) {
            clearInterval(interval);
            return 100;
          }
          return prev + 10;
        });
      }, 300);

      const response = await fetch('http://localhost:5000/api/train', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const result = await response.json();

      if (response.ok) {
        alert('Training Dataset Completed!');
      } else {
        alert(`Error: ${result.message || 'Training failed'}`);
      }
    } catch (error) {
      alert(`Error: ${error.message || 'Failed to connect to the server'}`);
    } finally {
      setTimeout(() => {
        setIsTraining(false);
        setProgress(0);
      }, 1000);
    }
  };

  return (
    <div className="min-h-screen relative overflow-hidden">
      {/* Background Image with Overlay */}
      <div
        className="absolute inset-0 z-0"
        style={{
          backgroundImage: 'url(/images/at.jpeg)',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
        }}
      />
      <div className="absolute inset-0 bg-black bg-opacity-60"></div>

      {/* Animated Particles */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {[...Array(15)].map((_, i) => (
          <div
            key={i}
            className="particle"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 5}s`,
            }}
          ></div>
        ))}
      </div>

      {/* Header */}
      <div className="relative z-10">
        <div className="bg-gradient-to-r from-blue-800 to-indigo-600 text-white p-4 flex justify-between items-center rounded-b-2xl shadow-lg">
          <button onClick={onBack} className="flex items-center bg-gray-600 text-white px-4 py-2 rounded-lg hover:bg-gray-700 hover:scale-105 transition-all shadow-md">
            <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 19l-7-7 7-7"></path>
            </svg>
            Back Home
          </button>
          <h1 className="text-3xl font-bold font-['Montserrat',_sans-serif] tracking-wide flex-1 text-center">
            Welcome to Training Panel
          </h1>
          <div className="w-12 h-12"></div>
        </div>
      </div>

      {/* Main Content */}
      <div className="relative z-10 flex justify-center items-center min-h-[calc(100vh-80px)] px-4">
        <div className="flex flex-col items-center bg-white p-8 rounded-2xl shadow-lg">
          <div className="relative group">
            <button
              onClick={trainClassifier}
              className="w-48 h-48 bg-gray-100 flex items-center justify-center rounded-2xl border-4 border-transparent hover:border-blue-500 transition-all duration-300 shadow-md"
              disabled={isTraining}
            >
              <img
                src="/images/t_btn1.png"
                alt="Train Dataset"
                className={`w-full h-full object-cover rounded-2xl ${isTraining ? 'animate-pulse' : ''}`}
              />
            </button>
            {/* Glowing Hover Effect */}
            <div className="absolute inset-0 rounded-2xl shadow-[0_0_20px_rgba(59,130,246,0.5)] opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none"></div>
            {/* Tooltip */}
            <div className="absolute top-0 left-1/2 transform -translate-x-1/2 -translate-y-12 bg-gray-800 text-white text-sm px-3 py-1 rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none">
              Click to train the dataset for face recognition
            </div>
          </div>
          <button
            onClick={trainClassifier}
            className="mt-6 w-48 bg-gradient-to-r from-blue-600 to-indigo-600 text-white font-bold py-3 rounded-xl hover:scale-105 hover:shadow-xl transition-all duration-300 shadow-md flex items-center justify-center"
            disabled={isTraining}
          >
            {isTraining ? (
              <svg className="w-5 h-5 mr-2 animate-spin" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 12a8 8 0 0116 0 8 8 0 01-16 0zm8-8a8 8 0 00-8 8"></path>
              </svg>
            ) : (
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 12H9m6 0a3 3 0 11-6 0 3 3 0 016 0z"></path>
              </svg>
            )}
            Train Dataset
          </button>
          {isTraining && (
            <div className="mt-4 w-48 bg-gray-200 rounded-full h-2.5">
              <div
                className="bg-blue-600 h-2.5 rounded-full transition-all duration-300"
                style={{ width: `${progress}%` }}
              ></div>
            </div>
          )}
        </div>
      </div>

      {/* CSS for Animations and Effects */}
      <style>
        {`
          @keyframes float {
            0%, 100% { transform: translateY(0); }
            50% { transform: translateY(-10px); }
          }
          .particle {
            position: absolute;
            width: 4px;
            height: 4px;
            background: rgba(59, 130, 246, 0.7);
            border-radius: 50%;
            animation: float 5s infinite ease-in-out;
            box-shadow: 0 0 10px rgba(59, 130, 246, 0.5);
          }
        `}
      </style>
    </div>
  );
}

export default Train;
