import React, { useState, useEffect, useRef } from 'react';

function HelpSupport({ onBack }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [errorMsg, setErrorMsg] = useState('');
  const chatAreaRef = useRef(null);

  // Scroll to the bottom of the chat area whenever messages change
  useEffect(() => {
    if (chatAreaRef.current) {
      chatAreaRef.current.scrollTop = chatAreaRef.current.scrollHeight;
    }
  }, [messages]);

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleSend = () => {
    if (!input.trim()) {
      setErrorMsg('Please enter some input');
      return;
    }

    setErrorMsg('');
    const userMessage = `You: ${input}`;
    setMessages((prev) => [...prev, userMessage]);

    const userInput = input.trim().toLowerCase();
    let botResponse = '';

    if (userInput === 'hello') {
      botResponse = 'Bot: Hi';
    } else if (userInput === 'how are you') {
      botResponse = 'Bot: fine and u';
    } else if (userInput === 'who created you') {
      botResponse = 'Bot: my developer known as A^3';
    } else if (userInput === 'what is your name') {
      botResponse = 'Bot: My name is mini Bot';
    } else if (userInput === 'bye') {
      botResponse = 'Bot: Thanks you for chatting';
    } else if (userInput === 'hwo does face recognition work') {
      botResponse = 'Bot: Face recognition works by detecting a face in an image, extracting unique features from it, and then comparing these features with stored data to identify the person.';
    } else {
      botResponse = "Bot: sorry i don't get it";
    }

    setMessages((prev) => [...prev, botResponse]);
    setInput('');
  };

  const handleClear = () => {
    setMessages([]);
    setInput('');
    setErrorMsg('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      {/* Header */}
      <div className="bg-blue-900 text-white p-4 flex justify-between items-center">
        <button onClick={onBack} className="bg-gray-500 text-white px-4 py-2 rounded">
          Back Home
        </button>
        <div className="flex items-center">
          <img src="/images/chatbot.jpg" alt="ChatBot" className="w-16 h-16 mr-2" />
          <h1 className="text-3xl font-bold">CHAT ME</h1>
        </div>
        <div></div>
      </div>

      {/* Main Frame */}
      <div className="bg-blue-200 p-4 mt-4 rounded">
        {/* Chat Area */}
        <div
          ref={chatAreaRef}
          className="bg-white p-4 h-96 overflow-y-auto rounded border-2 border-gray-300"
        >
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`mb-2 ${
                msg.startsWith('You:') ? 'text-right' : 'text-left'
              }`}
            >
              <span
                className={`inline-block p-2 rounded ${
                  msg.startsWith('You:') ? 'bg-blue-100' : 'bg-gray-100'
                }`}
              >
                {msg}
              </span>
            </div>
          ))}
        </div>

        {/* Input and Buttons */}
        <div className="bg-white p-4 mt-4 rounded flex items-center">
          <label className="text-green-700 font-bold mr-2">TYPE SOMETHING</label>
          <input
            type="text"
            value={input}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
            className="flex-1 p-2 border rounded mr-2"
            style={{ fontFamily: 'Times New Roman', fontSize: '16px', fontWeight: 'bold' }}
          />
          <button
            onClick={handleSend}
            className="bg-green-500 text-white px-4 py-2 rounded mr-2"
          >
            Send
          </button>
          <button
            onClick={handleClear}
            className="bg-red-500 text-white px-4 py-2 rounded"
          >
            ClearData
          </button>
        </div>

        {/* Error Message */}
        {errorMsg && (
          <div className="mt-2 text-red-500 font-bold">{errorMsg}</div>
        )}
      </div>
    </div>
  );
}

export default HelpSupport;
