import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function LoadingScreen() {
  const navigate = useNavigate();

  useEffect(() => {
    console.log('LoadingScreen mounted, starting 6-second timer...');

    // Redirect to the main page after 6 seconds
    const timer = setTimeout(() => {
      console.log('6 seconds elapsed, navigating to main page...');
      navigate('/', { replace: true }); // Use replace to avoid history stack issues
    }, 6000);

    return () => {
      console.log('LoadingScreen unmounting, clearing timer...');
      clearTimeout(timer);
    };
  }, [navigate]);

  return (
    <div className="fixed inset-0 bg-gray-900 flex items-center justify-center z-50">
      <img
        src="/images/f_det1.gif"
        alt="Loading Animation"
        className="w-full h-full object-cover"
      />
    </div>
  );
}

export default LoadingScreen;
