import React, { useState, useRef } from 'react';
import Webcam from 'react-webcam';

function FaceRecognition({ onBack }) {
  const webcamRef = useRef(null);
  const [result, setResult] = useState(null);

  const handleFaceDetect = async () => {
    const imageSrc = webcamRef.current.getScreenshot();
    if (!imageSrc) {
      alert('Failed to capture image from webcam.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/recognize-face', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ image: imageSrc }),
      });
      const data = await response.json();
      setResult(data);

      if (data.status === 200) {
        alert(data.message);
      } else {
        alert(data.message);
      }
    } catch (error) {
      alert('Error: ' + error.message);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      {/* Header */}
      <div className="bg-blue-900 text-white p-4 flex justify-between items-center">
        <button onClick={onBack} className="bg-gray-500 text-white px-4 py-2 rounded">
          Back Home
        </button>
        <h1 className="text-3xl font-bold">Welcome to Face Recognition Panel</h1>
        <div></div>
      </div>

      <div className="flex justify-center mt-8">
        <div className="text-center">
          <Webcam
            audio={false}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            width={640}
            height={480}
            className="mb-4 rounded"
          />
          <button
            onClick={handleFaceDetect}
            className="bg-white text-blue-900 font-bold py-2 px-4 rounded"
          >
            Face Detector
          </button>
          {result && result.student_info && (
            <div className="mt-4 text-left">
              <p><strong>Name:</strong> {result.student_info.name}</p>
              <p><strong>ID:</strong> {result.student_info.id}</p>
              <p><strong>Roll No:</strong> {result.student_info.roll_no}</p>
              <p><strong>Department:</strong> {result.student_info.department}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default FaceRecognition;
