import React from 'react';

function FaceDetector({ onBack }) {
  const handleFaceRecognition = async () => {
    // Clear session before starting new face recognition
    await fetch('http://localhost:5000/api/clear-session', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
    });

    // Simulate face recognition by sending a student_id (in a real implementation, you'd send an image)
    const response = await fetch('http://localhost:5000/api/mark-attendance', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ student_id: '1' }), // Simulate recognizing student with ID 1
    });
    const data = await response.json();
    alert(data.message);

    if (data.status === 200 && data.student) {
      const { id, name, roll, department } = data.student;
      alert(`Recognized Student:\nID: ${id}\nName: ${name}\nRoll: ${roll}\nDepartment: ${department}`);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <div className="bg-blue-900 text-white p-4 flex justify-between items-center">
        <button onClick={onBack} className="bg-gray-500 text-white px-4 py-2 rounded">
          Back Home
        </button>
        <h1 className="text-3xl font-bold">Welcome to Face Recognition Panel</h1>
        <div></div>
      </div>

      {/* Main Content */}
      <div className="flex justify-center mt-10">
        <div className="flex flex-col items-center">
          <button onClick={handleFaceRecognition} className="w-48 h-48 bg-gray-300 flex items-center justify-center">
            <img src="/images/f_det.jpg" alt="Face Detector" className="w-full h-full object-cover" />
          </button>
          <button onClick={handleFaceRecognition} className="mt-4 w-48 bg-white text-blue-900 font-bold py-2">
            Face Detector
          </button>
        </div>
      </div>
    </div>
  );
}

export default FaceDetector;
