import React, { useState, useEffect } from 'react';
import './App.css';
import Login from './components/Login';
import Register from './components/Register';
import Attendance from './components/Attendance';
import Dashboard from './components/Dashboard';

function App() {
  const [time, setTime] = useState(new Date().toLocaleTimeString());

  // Update time every second
  useEffect(() => {
    const timer = setInterval(() => {
      setTime(new Date().toLocaleTimeString());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  // Placeholder function for navigation or API calls
  const handleStudentPanel = () => {
    alert('Navigating to Student Panel...');
    // Replace with navigation logic if using React Router
  };

  const handleFaceDetector = async () => {
    // Example: Call the backend API to mark attendance
    const response = await fetch('http://localhost:5000/api/mark-attendance', {
      method: 'POST',
      headers: { 'Content-Type': 'multipart/form-data' },
      // Add image file if needed
    });
    const data = await response.json();
    alert(data.message);
  };

  const handleAttendance = () => {
    alert('Navigating to Attendance...');
  };

  const handleHelpSupport = () => {
    alert('Navigating to Help Support...');
  };

  const handleDataTrain = () => {
    alert('Starting Data Training...');
  };

  const handlePhotos = () => {
    alert('Opening Photos...');
  };

  const handleDevelopers = () => {
    alert('Navigating to Developers...');
  };

  const handleExit = () => {
    if (window.confirm('Are you sure you want to exit?')) {
      window.close(); // Note: This may not work in all browsers due to security restrictions
    }
  };

  const handleChatBot = () => {
    alert('Opening ChatBot...');
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <div className="bg-blue-900 text-white flex items-center justify-between p-2">
        <div className="bg-black text-white px-4 py-2">{time}</div>
        <h1 className="text-3xl font-bold text-center flex-1">
          Attendance Management System Using Facial Recognition
        </h1>
      </div>

      {/* Main Content */}
      <div className="relative p-8">
        {/* Grid of Buttons */}
        <div className="grid grid-cols-4 gap-8 max-w-6xl mx-auto">
          {/* Student Panel */}
          <div className="flex flex-col items-center">
            <button onClick={handleStudentPanel} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/std1.jpg" alt="Student Panel" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleStudentPanel} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Student Panel
            </button>
          </div>

          {/* Face Detector */}
          <div className="flex flex-col items-center">
            <button onClick={handleFaceDetector} className="w-56 h-56 bg-gray-500 flex items-center justify-center">
              <img src="/images/det1.jpg" alt="Face Detector" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleFaceDetector} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Face Detector
            </button>
          </div>

          {/* Attendance */}
          <div className="flex flex-col items-center">
            <button onClick={handleAttendance} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/att.jpg" alt="Attendance" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleAttendance} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Attendance
            </button>
          </div>

          {/* Help Support */}
          <div className="flex flex-col items-center">
            <button onClick={handleHelpSupport} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/hlp.jpg" alt="Help Support" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleHelpSupport} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Help Support
            </button>
          </div>

          {/* Data Train */}
          <div className="flex flex-col items-center">
            <button onClick={handleDataTrain} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/tra1.jpg" alt="Data Train" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleDataTrain} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Data Train
            </button>
          </div>

          {/* Photos */}
          <div className="flex flex-col items-center">
            <button onClick={handlePhotos} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/people.png" alt="Photos" className="w-full h-full object-cover" />
            </button>
            <button onClick={handlePhotos} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Photos
            </button>
          </div>

          {/* Developers */}
          <div className="flex flex-col items-center">
            <button onClick={handleDevelopers} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/develop.jpg" alt="Developers" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleDevelopers} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Developers
            </button>
          </div>

          {/* Exit */}
          <div className="flex flex-col items-center">
            <button onClick={handleExit} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/exit1.jpg" alt="Exit" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleExit} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Exit
            </button>
          </div>
        </div>

        {/* ChatBot Floating Button */}
        <div className="absolute bottom-20 right-20 flex flex-col items-center">
          <button onClick={handleChatBot} className="w-16 h-16 rounded-full bg-white border-2 border-gray-300 flex items-center justify-center">
            <img src="/chatbot.jpg" alt="ChatBot" className="w-full h-full rounded-full object-cover" />
          </button>
          <button onClick={handleChatBot} className="mt-2 w-16 bg-white text-blue-900 font-bold py-1">
            Bot
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
