import React, { useState, useEffect, useRef } from 'react';
import Webcam from 'react-webcam';

function Student({ onBack }) {
  const [formData, setFormData] = useState({
    student_id: '',
    name: '',
    department: 'Select Department',
    course: 'Select Course',
    year: 'Select Year',
    semester: 'Select Semester',
    division: 'A',
    gender: 'Male',
    dob: '',
    phone: '',
    address: '',
    roll_no: '',
    email: '',
    teacher: '',
    photo_sample: 'No',
  });

  const [students, setStudents] = useState([]);
  const [searchRollNo, setSearchRollNo] = useState('');
  const [capturedPhoto, setCapturedPhoto] = useState(null);
  const [message, setMessage] = useState({ text: '', type: '' }); // For success/error messages
  const [errors, setErrors] = useState({ email: '', phone: '' }); // For real-time validation
  const webcamRef = useRef(null);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/students');
      const data = await response.json();
      if (data.status === 200) {
        setStudents(data.students);
      } else {
        setMessage({ text: data.message, type: 'error' });
      }
    } catch (error) {
      console.error('Error fetching students:', error);
      setMessage({ text: 'Failed to fetch students. Please check the console for details.', type: 'error' });
    }
  };

  const checkStudentIdExists = async (student_id, excludeId = null) => {
    try {
      const response = await fetch(`http://localhost:5000/api/students/check-id?student_id=${student_id}`);
      const data = await response.json();
      if (data.status === 200) {
        const exists = data.exists;
        if (exists) {
          const existingStudent = data.student;
          if (excludeId && existingStudent.student_id === excludeId) {
            return false;
          }
          return true;
        }
      }
      return false;
    } catch (error) {
      console.error('Error checking student ID:', error);
      setMessage({ text: 'Failed to check student ID. Please check the console for details.', type: 'error' });
      return false;
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // Real-time validation for email and phone
    if (name === 'email') {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      setErrors({ ...errors, email: emailRegex.test(value) ? '' : 'Please enter a valid email address.' });
    }
    if (name === 'phone') {
      const phoneRegex = /^\d{10}$/;
      setErrors({ ...errors, phone: phoneRegex.test(value) ? '' : 'Please enter a valid 10-digit phone number.' });
    }
  };

  const validateDob = (dob) => {
    const dobRegex = /^\d{4}-\d{2}-\d{2}$/;
    return dobRegex.test(dob);
  };

  const handleAdd = async () => {
    const requiredFields = [
      'student_id', 'name', 'department', 'course', 'year', 'semester',
      'division', 'gender', 'dob', 'phone', 'address', 'roll_no', 'email', 'teacher',
    ];
    if (requiredFields.some(field => !formData[field] || formData[field].includes('Select'))) {
      setMessage({ text: 'Please fill all fields!', type: 'error' });
      return;
    }

    if (!validateDob(formData.dob)) {
      setMessage({ text: 'Please enter DOB in the format YYYY-MM-DD (e.g., 2000-05-15)!', type: 'error' });
      return;
    }

    if (errors.email || errors.phone) {
      setMessage({ text: 'Please correct the errors in the form!', type: 'error' });
      return;
    }

    const idExists = await checkStudentIdExists(formData.student_id);
    if (idExists) {
      setMessage({ text: `Student ID ${formData.student_id} already exists. Please use a different ID.`, type: 'error' });
      return;
    }

    const dataToSend = { ...formData, photo: capturedPhoto || null };
    console.log('Sending data to backend:', dataToSend);

    try {
      const response = await fetch('http://localhost:5000/api/students', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(dataToSend),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      console.log('Backend response:', data);
      setMessage({ text: data.message, type: data.status === 200 ? 'success' : 'error' });

      if (data.status === 200) {
        fetchStudents();
        handleReset();
      }
    } catch (error) {
      console.error('Error adding student:', error);
      setMessage({ text: 'Failed to add student. Please check the console for details.', type: 'error' });
    }
  };

  const handleUpdate = async () => {
    if (!formData.student_id) {
      setMessage({ text: 'Please select a student to update!', type: 'error' });
      return;
    }

    if (!validateDob(formData.dob)) {
      setMessage({ text: 'Please enter DOB in the format YYYY-MM-DD (e.g., 2000-05-15)!', type: 'error' });
      return;
    }

    if (errors.email || errors.phone) {
      setMessage({ text: 'Please correct the errors in the form!', type: 'error' });
      return;
    }

    const idExists = await checkStudentIdExists(formData.student_id, formData.student_id);
    if (idExists) {
      setMessage({ text: `Student ID ${formData.student_id} is already taken by another student. Please use a different ID.`, type: 'error' });
      return;
    }

    const dataToSend = { ...formData, photo: capturedPhoto || null };
    console.log('Sending update data to backend:', dataToSend);

    try {
      const response = await fetch(`http://localhost:5000/api/students/${formData.student_id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(dataToSend),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      console.log('Backend response:', data);
      setMessage({ text: data.message, type: data.status === 200 ? 'success' : 'error' });

      if (data.status === 200) {
        fetchStudents();
        handleReset();
      }
    } catch (error) {
      console.error('Error updating student:', error);
      setMessage({ text: 'Failed to update student. Please check the console for details.', type: 'error' });
    }
  };

  const handleDelete = async () => {
    if (!formData.student_id) {
      setMessage({ text: 'Please select a student to delete!', type: 'error' });
      return;
    }

    if (!window.confirm('Do you want to delete this student?')) return;

    try {
      const response = await fetch(`http://localhost:5000/api/students/${formData.student_id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setMessage({ text: data.message, type: data.status === 200 ? 'success' : 'error' });

      if (data.status === 200) {
        fetchStudents();
        handleReset();
      }
    } catch (error) {
      console.error('Error deleting student:', error);
      setMessage({ text: 'Failed to delete student. Please check the console for details.', type: 'error' });
    }
  };

  const handleReset = () => {
    setFormData({
      student_id: '',
      name: '',
      department: 'Select Department',
      course: 'Select Course',
      year: 'Select Year',
      semester: 'Select Semester',
      division: 'A',
      gender: 'Male',
      dob: '',
      phone: '',
      address: '',
      roll_no: '',
      email: '',
      teacher: '',
      photo_sample: 'No',
    });
    setCapturedPhoto(null);
    setErrors({ email: '', phone: '' });
  };

  const handleSearch = async () => {
    if (!searchRollNo) {
      setMessage({ text: 'Please enter a roll number to search!', type: 'error' });
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/api/students/search?roll_no=${searchRollNo}`);
      const data = await response.json();
      if (data.status === 200) {
        setStudents(data.students);
      } else {
        setMessage({ text: data.message, type: 'error' });
      }
    } catch (error) {
      console.error('Error searching students:', error);
      setMessage({ text: 'Failed to search students. Please check the console for details.', type: 'error' });
    }
  };

  const handleRowClick = (student) => {
    setFormData(student);
    setCapturedPhoto(student.photo || null);
  };

  const handleTakePhoto = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    if (imageSrc) {
      setCapturedPhoto(imageSrc);
      console.log('Photo captured successfully:', imageSrc.substring(0, 50) + '...');
    } else {
      setMessage({ text: 'Failed to capture photo. Please ensure the camera is accessible.', type: 'error' });
    }
  };

  const handleDeletePhoto = () => {
    setCapturedPhoto(null);
    console.log('Photo deleted');
  };

  return (
    <div
      className="min-h-screen p-6 relative bg-gray-100"
      style={{
        backgroundImage: 'url(/images/bb.jpg)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
      }}
    >
      {/* Background Overlay */}
      <div className="absolute inset-0 bg-black bg-opacity-50"></div>

      {/* Main Content */}
      <div className="relative z-10">
        {/* Navigation Bar */}
        <div className="bg-blue-900 text-white p-4 flex justify-between items-center rounded-lg shadow-lg">
          <button onClick={onBack} className="bg-gradient-to-r from-gray-500 to-gray-600 text-white px-4 py-2 rounded-lg hover:from-gray-600 hover:to-gray-700 transition-all">
            Back Home
          </button>
          <h1 className="text-3xl font-extrabold text-center">Welcome to Student Panel</h1>
          <div></div>
        </div>

        {/* Message Notification */}
        {message.text && (
          <div className={`mt-4 p-4 rounded-lg shadow-lg text-white ${message.type === 'success' ? 'bg-green-500' : 'bg-red-500'} transition-all duration-300`}>
            {message.text}
          </div>
        )}

        {/* Main Form Section */}
        <div className="mt-6 bg-gray-50 p-6 rounded-lg shadow-lg">
          <div className="flex flex-col lg:flex-row gap-6">
            {/* Left Section: Student Details */}
            <div className="w-full lg:w-1/2">
              <h2 className="text-2xl font-bold text-blue-900 mb-4">Student Details</h2>

              {/* Current Course Section */}
              <div className="bg-blue-50 p-4 rounded-lg shadow-sm mb-4">
                <h3 className="text-xl font-semibold text-blue-900 mb-3">Current Course</h3>
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-gray-800 font-semibold text-sm mb-1">Department</label>
                    <select
                      name="department"
                      value={formData.department}
                      onChange={handleInputChange}
                      className="w-full p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800"
                    >
                      <option>Select Department</option>
                      <option>Computer</option>
                      <option>IT</option>
                      <option>Civil</option>
                      <option>Mechanical</option>
                      <option>Electrical</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-gray-800 font-semibold text-sm mb-1">Course</label>
                    <select
                      name="course"
                      value={formData.course}
                      onChange={handleInputChange}
                      className="w-full p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800"
                    >
                      <option>Select Course</option>
                      <option>SE</option>
                      <option>FE</option>
                      <option>TE</option>
                      <option>BE</option>
                      <option>MS</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-gray-800 font-semibold text-sm mb-1">Year</label>
                    <select
                      name="year"
                      value={formData.year}
                      onChange={handleInputChange}
                      className="w-full p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800"
                    >
                      <option>Select Year</option>
                      <option>2017-21</option>
                      <option>2018-22</option>
                      <option>2019-23</option>
                      <option>2020-24</option>
                      <option>2021-25</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-gray-800 font-semibold text-sm mb-1">Semester</label>
                    <select
                      name="semester"
                      value={formData.semester}
                      onChange={handleInputChange}
                      className="w-full p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800"
                    >
                      <option>Select Semester</option>
                      <option>Semester-1</option>
                      <option>Semester-2</option>
                      <option>Semester-3</option>
                      <option>Semester-4</option>
                      <option>Semester-5</option>
                      <option>Semester-6</option>
                      <option>Semester-7</option>
                      <option>Semester-8</option>
                    </select>
                  </div>
                </div>
              </div>

              {/* Class Student Information Section */}
              <div className="bg-blue-50 p-4 rounded-lg shadow-sm">
                <h3 className="text-xl font-semibold text-blue-900 mb-3">Class Student Information</h3>
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-gray-800 font-semibold text-sm mb-1">Student ID</label>
                    <input
                      type="text"
                      name="student_id"
                      value={formData.student_id}
                      onChange={handleInputChange}
                      className="w-full p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800"
                    />
                  </div>
                  <div>
                    <label className="block text-gray-800 font-semibold text-sm mb-1">Student Name</label>
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      className="w-full p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800"
                    />
                  </div>
                  <div>
                    <label className="block text-gray-800 font-semibold text-sm mb-1">Class Division</label>
                    <select
                      name="division"
                      value={formData.division}
                      onChange={handleInputChange}
                      className="w-full p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800"
                    >
                      <option>A</option>
                      <option>B</option>
                      <option>C</option>
                      <option>D</option>
                      <option>E</option>
                      <option>F</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-gray-800 font-semibold text-sm mb-1">Roll No</label>
                    <input
                      type="text"
                      name="roll_no"
                      value={formData.roll_no}
                      onChange={handleInputChange}
                      className="w-full p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800"
                    />
                  </div>
                  <div>
                    <label className="block text-gray-800 font-semibold text-sm mb-1">Gender</label>
                    <select
                      name="gender"
                      value={formData.gender}
                      onChange={handleInputChange}
                      className="w-full p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800"
                    >
                      <option>Male</option>
                      <option>Female</option>
                      <option>Others</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-gray-800 font-semibold text-sm mb-1">DOB</label>
                    <input
                      type="date"
                      name="dob"
                      value={formData.dob}
                      onChange={handleInputChange}
                      className="w-full p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800"
                    />
                  </div>
                  <div>
                    <label className="block text-gray-800 font-semibold text-sm mb-1">Email</label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      className={`w-full p-2 border rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800 ${errors.email ? 'border-red-500' : 'border-gray-300'}`}
                    />
                    {errors.email && <p className="text-red-500 text-xs mt-1">{errors.email}</p>}
                  </div>
                  <div>
                    <label className="block text-gray-800 font-semibold text-sm mb-1">Phone</label>
                    <input
                      type="text"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                      className={`w-full p-2 border rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800 ${errors.phone ? 'border-red-500' : 'border-gray-300'}`}
                    />
                    {errors.phone && <p className="text-red-500 text-xs mt-1">{errors.phone}</p>}
                  </div>
                  <div>
                    <label className="block text-gray-800 font-semibold text-sm mb-1">Address</label>
                    <input
                      type="text"
                      name="address"
                      value={formData.address}
                      onChange={handleInputChange}
                      className="w-full p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800"
                    />
                  </div>
                  <div>
                    <label className="block text-gray-800 font-semibold text-sm mb-1">Teacher Name</label>
                    <input
                      type="text"
                      name="teacher"
                      value={formData.teacher}
                      onChange={handleInputChange}
                      className="w-full p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800"
                    />
                  </div>
                </div>
                <div className="mt-4 flex space-x-4">
                  <label className="flex items-center text-gray-800">
                    <input
                      type="radio"
                      name="photo_sample"
                      value="Yes"
                      checked={formData.photo_sample === 'Yes'}
                      onChange={handleInputChange}
                      className="mr-2"
                    />
                    Take Photo Sample
                  </label>
                  <label className="flex items-center text-gray-800">
                    <input
                      type="radio"
                      name="photo_sample"
                      value="No"
                      checked={formData.photo_sample === 'No'}
                      onChange={handleInputChange}
                      className="mr-2"
                    />
                    No Photo Sample
                  </label>
                </div>
                {formData.photo_sample === 'Yes' && (
                  <div className="mt-4 transition-all duration-300">
                    <h3 className="text-xl font-semibold text-blue-900 mb-3">Capture Photo</h3>
                    <div className="flex flex-col items-center">
                      {!capturedPhoto ? (
                        <Webcam
                          audio={false}
                          ref={webcamRef}
                          screenshotFormat="image/jpeg"
                          width={320}
                          height={240}
                          videoConstraints={{
                            facingMode: 'user',
                          }}
                          className="rounded-lg border border-gray-300 shadow-sm"
                        />
                      ) : (
                        <img
                          src={capturedPhoto}
                          alt="Captured Photo"
                          className="w-80 h-60 object-cover rounded-lg border border-gray-300 shadow-sm"
                        />
                      )}
                    </div>
                  </div>
                )}
              </div>

              {/* Action Buttons */}
              <div className="mt-4 flex flex-wrap gap-2">
                <button onClick={handleAdd} className="bg-gradient-to-r from-blue-900 to-blue-700 text-white px-4 py-2 rounded-lg shadow hover:from-blue-800 hover:to-blue-600 transition-all">
                  Save
                </button>
                <button onClick={handleUpdate} className="bg-gradient-to-r from-blue-900 to-blue-700 text-white px-4 py-2 rounded-lg shadow hover:from-blue-800 hover:to-blue-600 transition-all">
                  Update
                </button>
                <button onClick={handleDelete} className="bg-gradient-to-r from-blue-900 to-blue-700 text-white px-4 py-2 rounded-lg shadow hover:from-blue-800 hover:to-blue-600 transition-all">
                  Delete
                </button>
                <button onClick={handleReset} className="bg-gradient-to-r from-blue-900 to-blue-700 text-white px-4 py-2 rounded-lg shadow hover:from-blue-800 hover:to-blue-600 transition-all">
                  Reset
                </button>
                {formData.photo_sample === 'Yes' && (
                  <>
                    <button onClick={handleTakePhoto} className="bg-gradient-to-r from-blue-900 to-blue-700 text-white px-4 py-2 rounded-lg shadow hover:from-blue-800 hover:to-blue-600 transition-all">
                      Take Pic
                    </button>
                    <button onClick={handleDeletePhoto} className="bg-gradient-to-r from-blue-900 to-blue-700 text-white px-4 py-2 rounded-lg shadow hover:from-blue-800 hover:to-blue-600 transition-all">
                      Delete Pic
                    </button>
                  </>
                )}
              </div>
            </div>

            {/* Right Section: Search System */}
            <div className="w-full lg:w-1/2">
              <h2 className="text-2xl font-bold text-blue-900 mb-4">Student Records</h2>

              {/* Search System */}
              <div className="bg-blue-50 p-4 rounded-lg shadow-sm mb-4">
                <h3 className="text-xl font-semibold text-blue-900 mb-3">Search System</h3>
                <div className="flex flex-wrap gap-2">
                  <select className="p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800">
                    <option>Roll-No</option>
                  </select>
                  <input
                    type="text"
                    value={searchRollNo}
                    onChange={(e) => setSearchRollNo(e.target.value)}
                    placeholder="Enter Roll No to Search"
                    className="p-2 border border-gray-300 rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 text-gray-800 flex-1"
                  />
                  <button onClick={handleSearch} className="bg-gradient-to-r from-blue-900 to-blue-700 text-white px-4 py-2 rounded-lg shadow hover:from-blue-800 hover:to-blue-600 transition-all">
                    Search
                  </button>
                  <button onClick={fetchStudents} className="bg-gradient-to-r from-blue-900 to-blue-700 text-white px-4 py-2 rounded-lg shadow hover:from-blue-800 hover:to-blue-600 transition-all">
                    Show All
                  </button>
                </div>
              </div>

              {/* Student Records Table */}
              <div className="bg-blue-50 p-4 rounded-lg shadow-sm max-h-96 overflow-x-auto">
                <table className="w-full border-collapse">
                  <thead>
                    <tr className="bg-blue-900 text-white sticky top-0 z-10">
                      <th className="border border-gray-400 p-2">ID</th>
                      <th className="border border-gray-400 p-2">Name</th>
                      <th className="border border-gray-400 p-2">Dep</th>
                      <th className="border border-gray-400 p-2">Course</th>
                      <th className="border border-gray-400 p-2">Year</th>
                      <th className="border border-gray-400 p-2">Sem</th>
                      <th className="border border-gray-400 p-2">Div</th>
                      <th className="border border-gray-400 p-2">Gender</th>
                      <th className="border border-gray-400 p-2">DOB</th>
                      <th className="border border-gray-400 p-2">Phone</th>
                      <th className="border border-gray-400 p-2">Address</th>
                      <th className="border border-gray-400 p-2">Roll-No</th>
                      <th className="border border-gray-400 p-2">Email</th>
                      <th className="border border-gray-400 p-2">Teacher</th>
                      <th className="border border-gray-400 p-2">Photo</th>
                    </tr>
                  </thead>
                  <tbody>
                    {students.map((student) => (
                      <tr
                        key={student.student_id}
                        onClick={() => handleRowClick(student)}
                        className="cursor-pointer hover:bg-blue-100 transition-all"
                      >
                        <td className="border border-gray-400 p-2 text-gray-800">{student.student_id}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{student.name}</td>
                        <td className-replace-className="border border-gray-400 p-2 text-gray-800">{student.department}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{student.course}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{student.year}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{student.semester}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{student.division}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{student.gender}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{student.dob}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{student.phone}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{student.address}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{student.roll_no}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{student.email}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{student.teacher}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{student.photo ? 'Yes' : 'No'}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Student;
