import React, { useState, useEffect, useRef } from 'react';

function Attendance({ onBack }) {
  const [csvData, setCsvData] = useState([]);
  const [attendanceData, setAttendanceData] = useState([]);
  const [csvInput, setCsvInput] = useState('');
  const [selectedRow, setSelectedRow] = useState(null); // Track selected row for update/delete
  const fileInputRef = useRef(null); // Ref for file input

  // Fetch attendance data from MySQL on component mount
  useEffect(() => {
    fetchAttendance();
  }, []);

  const fetchAttendance = async () => {
    const response = await fetch('http://localhost:5000/api/attendance');
    const data = await response.json();
    if (data.status === 200) {
      setAttendanceData(data.attendance);
    } else {
      alert(data.message);
    }
  };

  const handleCsvInputChange = (e) => {
    setCsvInput(e.target.value);
  };

  const handleImportCsv = () => {
    // Trigger the hidden file input click
    fileInputRef.current.click();
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // Validate file type
    if (!file.name.endsWith('.csv')) {
      alert('Please select a CSV file!');
      return;
    }

    // Read the file contents
    const reader = new FileReader();
    reader.onload = (event) => {
      const text = event.target.result;
      // Update the textarea with the file contents
      setCsvInput(text);

      // Parse the CSV data for the left table
      const rows = text.split('\n').filter(row => row.trim()).map(row => row.split(','));
      setCsvData(rows);
    };
    reader.onerror = () => {
      alert('Error reading the file!');
    };
    reader.readAsText(file);
  };

  const handleExportCsv = async () => {
    const response = await fetch('http://localhost:5000/api/export-csv');
    if (response.ok) {
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'attendance_export.csv';
      document.body.appendChild(a);
      a.click();
      a.remove();
      window.URL.revokeObjectURL(url);
      alert('CSV exported successfully!');
    } else {
      const data = await response.json();
      alert(data.message);
    }
  };

  const handleImportToDb = async () => {
    if (!csvInput) {
      alert('Please select a CSV file to import!');
      return;
    }

    const rows = csvInput.split('\n').filter(row => row.trim()).map(row => row.split(','));
    if (rows.length === 0) {
      alert('No valid CSV data provided!');
      return;
    }

    // Send CSV data to backend to save in MySQL
    const response = await fetch('http://localhost:5000/api/import-csv', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ csv_data: rows }),
    });
    const data = await response.json();
    alert(data.message);
    if (data.status === 200) {
      fetchAttendance(); // Refresh MySQL table
    }
  };

  const handleUpdate = async () => {
    if (!selectedRow) {
      alert('Please select an attendance record to update!');
      return;
    }

    const requiredFields = ['id', 'roll_no', 'name', 'time', 'date', 'status'];
    if (requiredFields.some(field => !selectedRow[field] || selectedRow[field] === 'Status')) {
      alert('Selected record is missing required fields!');
      return;
    }

    const response = await fetch(`http://localhost:5000/api/attendance/${selectedRow.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(selectedRow),
    });
    const data = await response.json();
    alert(data.message);
    if (data.status === 200) {
      fetchAttendance();
      setSelectedRow(null); // Clear selection
    }
  };

  const handleDelete = async () => {
    if (!selectedRow) {
      alert('Please select an attendance record to delete!');
      return;
    }

    if (!window.confirm('Do you want to delete this attendance record?')) return;

    const response = await fetch(`http://localhost:5000/api/attendance/${selectedRow.id}`, {
      method: 'DELETE',
    });
    const data = await response.json();
    alert(data.message);
    if (data.status === 200) {
      fetchAttendance();
      setSelectedRow(null); // Clear selection
    }
  };

  const handleAttendanceRowClick = (row) => {
    setSelectedRow(row); // Set the selected row for update/delete
  };

  return (
    <div
      className="min-h-screen p-8 relative"
      style={{
        backgroundImage: 'url(/images/bb.jpg)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
      }}
    >
      {/* Overlay to improve text readability */}
      <div className="absolute inset-0 bg-black bg-opacity-50"></div>

      {/* Hidden File Input */}
      <input
        type="file"
        ref={fileInputRef}
        onChange={handleFileChange}
        accept=".csv"
        className="hidden"
      />

      {/* Content */}
      <div className="relative z-10">
        {/* Header */}
        <div className="bg-blue-900 text-white p-4 flex justify-between items-center">
          <button onClick={onBack} className="bg-gray-500 text-white px-4 py-2 rounded">
            Back Home
          </button>
          <h1 className="text-3xl font-bold">Welcome to Attendance Panel</h1>
          <div></div>
        </div>

        <div className="bg-[#E6F0FA] p-6 rounded shadow mt-4">
          <div className="flex">
            {/* Left Section: CSV Table */}
            <div className="w-1/2 pr-4 border-r border-gray-300">
              <h2 className="text-xl font-bold text-blue-900 mb-4">Student Details</h2>

              {/* CSV Table */}
              <div className="border border-gray-300 p-4 max-h-80 overflow-auto bg-[#F0F7FF]">
                <table className="w-full border-collapse">
                  <thead>
                    <tr className="bg-blue-900 text-white">
                      <th className="border border-gray-400 p-2">Std-ID</th>
                      <th className="border border-gray-400 p-2">Roll.No</th>
                      <th className="border border-gray-400 p-2">Std-Name</th>
                      <th className="border border-gray-400 p-2">Time</th>
                      <th className="border border-gray-400 p-2">Date</th>
                      <th className="border border-gray-400 p-2">Attend-status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {csvData.map((row, index) => (
                      <tr
                        key={index}
                        className="cursor-pointer hover:bg-blue-100"
                      >
                        <td className="border border-gray-400 p-2 text-gray-800">{row[0]}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{row[1]}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{row[2]}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{row[3]}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{row[4]}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{row[5]}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {/* Buttons */}
              <div className="mt-4 flex space-x-2">
                <button onClick={handleImportCsv} className="bg-blue-900 text-white px-4 py-2 rounded">
                  Import CSV
                </button>
                <button onClick={handleExportCsv} className="bg-blue-900 text-white px-4 py-2 rounded">
                  Export CSV
                </button>
              </div>
            </div>

            {/* Right Section: MySQL Table */}
            <div className="w-1/2 pl-4">
              <h2 className="text-xl font-bold text-blue-900 mb-4">Student Details</h2>

              {/* CSV Input for Import */}
              <div className="border border-gray-300 p-4 mb-4 bg-[#F0F7FF]">
                <h3 className="text-lg font-bold text-blue-900 mb-2">Paste CSV Data</h3>
                <textarea
                  value={csvInput}
                  onChange={handleCsvInputChange}
                  className="w-full p-2 border border-gray-400 rounded text-gray-800"
                  rows="3"
                  placeholder="Paste CSV data here (e.g., Std-ID,Roll.No,Std-Name,Time,Date,Status)"
                />
                <button onClick={handleImportToDb} className="mt-2 bg-blue-900 text-white px-4 py-2 rounded">
                  Import to Database
                </button>
              </div>

              {/* MySQL Table */}
              <div className="border border-gray-300 p-4 max-h-96 overflow-auto bg-[#F0F7FF]">
                <table className="w-full border-collapse">
                  <thead>
                    <tr className="bg-blue-900 text-white">
                      <th className="border border-gray-400 p-2">Std-ID</th>
                      <th className="border border-gray-400 p-2">Roll.No</th>
                      <th className="border border-gray-400 p-2">Std-Name</th>
                      <th className="border border-gray-400 p-2">Time</th>
                      <th className="border border-gray-400 p-2">Date</th>
                      <th className="border border-gray-400 p-2">Attend-status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {attendanceData.map((row) => (
                      <tr
                        key={row.id}
                        onClick={() => handleAttendanceRowClick(row)}
                        className={`cursor-pointer hover:bg-blue-100 ${selectedRow && selectedRow.id === row.id ? 'bg-blue-200' : ''}`}
                      >
                        <td className="border border-gray-400 p-2 text-gray-800">{row.id}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{row.roll_no}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{row.name}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{row.time}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{row.date}</td>
                        <td className="border border-gray-400 p-2 text-gray-800">{row.status}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {/* Buttons */}
              <div className="mt-4 flex space-x-2">
                <button onClick={handleUpdate} className="bg-blue-900 text-white px-4 py-2 rounded">
                  Update
                </button>
                <button onClick={handleDelete} className="bg-blue-900 text-white px-4 py-2 rounded">
                  Delete
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Attendance;
