import React, { useState, useEffect, useRef } from 'react';

function ChatBot({ onBack }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isChatOpen, setIsChatOpen] = useState(false); // State to toggle chat window
  const chatEndRef = useRef(null);

  // Scroll to the bottom of the chat when messages update
  useEffect(() => {
    if (isChatOpen) {
      chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages, isChatOpen]);

  const handleSend = () => {
    const userInput = input.trim().toLowerCase();

    if (userInput) {
      setMessages((prev) => [...prev, { sender: 'You', text: userInput }]);

      // Bot responses
      if (userInput === 'hello') {
        setMessages((prev) => [...prev, { sender: 'Bot', text: 'Hi' }]);
      } else if (userInput === 'how are you') {
        setMessages((prev) => [...prev, { sender: 'Bot', text: 'fine and u' }]);
      } else if (userInput === 'who created you') {
        setMessages((prev) => [...prev, { sender: 'Bot', text: 'my developer known as A^3' }]);
      } else if (userInput === 'what is your name') {
        setMessages((prev) => [...prev, { sender: 'Bot', text: 'My name is mini Bot' }]);
      } else if (userInput === 'bye') {
        setMessages((prev) => [...prev, { sender: 'Bot', text: 'Thanks you for chatting' }]);
      } else if (userInput === 'hwo does face recognition work') {
        setMessages((prev) => [...prev, { sender: 'Bot', text: 'Face recognition works by detecting a face in an image, extracting unique features from it, and then comparing these features with stored data to identify the person.' }]);
      } else {
        setMessages((prev) => [...prev, { sender: 'Bot', text: "sorry i don't get it" }]);
      }

      setInput('');
    } else {
      alert('Please enter some input');
    }
  };

  const handleClear = () => {
    setMessages([]);
    setInput('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  const toggleDarkMode = () => {
    setIsDarkMode((prev) => !prev);
  };

  const toggleChat = () => {
    setIsChatOpen((prev) => !prev);
  };

  return (
    <div
      className="min-h-screen flex flex-col items-center relative"
      style={{
        backgroundImage: 'url(/images/bb.jpg)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
      }}
    >
      {/* Header */}
      <div className="w-full max-w-3xl bg-white shadow-md">
        <div className="flex items-center justify-between p-2">
          <button onClick={onBack} className="bg-gray-500 text-white px-4 py-2 rounded">
            Back Home
          </button>
          <div className="flex items-center">
            <h1 className="text-2xl font-bold text-green-600">CHAT ME</h1>
          </div>
          <button
            onClick={toggleDarkMode}
            className="bg-gray-500 text-white px-4 py-2 rounded"
          >
            {isDarkMode ? 'Light Mode' : 'Dark Mode'}
          </button>
        </div>
      </div>

      {/* Chat Window (Collapsible, opens within the main page) */}
      {isChatOpen && (
        <div className="fixed bottom-20 right-5 w-80 bg-white shadow-lg rounded-lg flex flex-col">
          {/* Chat Area */}
          <div
            className={`p-4 flex-grow overflow-y-auto h-64 ${
              isDarkMode ? 'bg-gray-800' : 'bg-blue-100'
            }`}
          >
            {messages.map((msg, index) => (
              <div
                key={index}
                className={`mb-2 ${msg.sender === 'You' ? 'text-right' : 'text-left'}`}
              >
                <span
                  className={`inline-block p-2 rounded-lg ${
                    msg.sender === 'You'
                      ? 'bg-blue-500 text-white'
                      : isDarkMode
                      ? 'bg-gray-600 text-white'
                      : 'bg-gray-300 text-black'
                  }`}
                >
                  <strong>{msg.sender}:</strong> {msg.text}
                </span>
              </div>
            ))}
            <div ref={chatEndRef} />
          </div>

          {/* Input Area */}
          <div className="p-4 border-t flex items-center gap-2">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={handleKeyPress}
              className="flex-grow p-2 border border-gray-300 rounded"
              style={{ fontFamily: 'Times New Roman', fontSize: '16px', fontWeight: 'bold' }}
              placeholder="Type something..."
            />
            <button
              onClick={handleSend}
              className="bg-green-500 text-white px-4 py-2 rounded font-bold"
            >
              Send
            </button>
            <button
              onClick={handleClear}
              className="bg-red-500 text-white px-4 py-2 rounded font-bold"
            >
              Clear
            </button>
          </div>
        </div>
      )}

      {/* Chat Bubble Icon (Bottom Right) */}
      <div
        className="fixed bottom-5 right-5 cursor-pointer"
        onClick={toggleChat}
      >
        <div className="relative">
          {/* Messenger-style chat bubble shape */}
          <div className="w-16 h-16 bg-green-500 rounded-lg flex items-center justify-center">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-8 w-8 text-white"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M8 10h.01M12 10h.01M16 10h.01M9 16H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-5l-5 5v-5z"
              />
            </svg>
          </div>
          {/* Tail of the chat bubble */}
          <div className="absolute bottom-0 right-0 w-0 h-0 border-t-8 border-t-green-500 border-l-8 border-l-transparent border-r-8 border-r-transparent transform translate-x-[-8px]"></div>
        </div>
      </div>
    </div>
  );
}

export default ChatBot;
