import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Register({ onBack }) {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    fname: '',
    lname: '',
    cnum: '',
    email: '',
    ssq: 'Select',
    sa: '',
    pwd: '',
    cpwd: '',
    check: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const validateForm = () => {
    const { fname, lname, cnum, email, ssq, sa, pwd, cpwd, check } = formData;

    if (!fname || !lname || !cnum || !email || ssq === 'Select' || !sa || !pwd || !cpwd) {
      alert('All Fields Are Required!');
      return false;
    }

    if (!email.includes('@')) {
      alert('Email must include @ symbol!');
      return false;
    }

    if (pwd !== cpwd) {
      alert('Passwords do not match!');
      return false;
    }

    const passwordRegex = /^(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9])(?=.*[!@#$%^&*(),.?":{}|<>]).+$/;
    if (!passwordRegex.test(pwd)) {
      alert('Password must contain an uppercase letter, lowercase letter, digit, and special character!');
      return false;
    }

    const contactRegex = /^\d+$/;
    if (!contactRegex.test(cnum)) {
      alert('Please enter a valid contact number with digits only!');
      return false;
    }

    if (cnum.length !== 10) {
      alert('Please enter a 10-digit contact number!');
      return false;
    }

    const nameRegex = /^[A-Za-z\s]+$/;
    if (!nameRegex.test(fname) || !nameRegex.test(lname)) {
      alert('Please enter a valid name only!');
      return false;
    }

    if (!fname[0]?.toUpperCase() === fname[0] || !lname[0]?.toUpperCase() === lname[0]) {
      alert('First letter should be capital!');
      return false;
    }

    if (!check) {
      alert('Please agree to the Terms and Conditions!');
      return false;
    }

    return true;
  };

  const handleRegister = async () => {
    if (!validateForm()) return;

    try {
      const response = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          fname: formData.fname,
          lname: formData.lname,
          cnum: formData.cnum,
          email: formData.email,
          ssq: formData.ssq,
          sa: formData.sa,
          pwd: formData.pwd,
        }),
      });

      const result = await response.json();

      if (response.ok) {
        // Store credentials in localStorage
        const username = `${formData.fname} ${formData.lname}`; // Combine fname and lname as username
        localStorage.setItem('username', username);
        localStorage.setItem('email', formData.email);
        localStorage.setItem('password', formData.pwd); // Note: Storing password in localStorage is not secure in production
        localStorage.setItem('loggedIn', 'true');
        window.dispatchEvent(new Event('storageChange'));

        alert('Successfully Registered!');
        navigate('/login');
      } else {
        alert(result.message || 'Registration failed');
      }
    } catch (error) {
      alert(`Error: ${error.message}`);
    }
  };

  const handleSwitchToLogin = () => {
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gray-100 relative">
      {/* Background Image */}
      <img src="/images/r.jpg" alt="Background" className="absolute inset-0 w-full h-full object-cover" />

      {/* Registration Frame */}
      <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-[#F2F2F2] w-[900px] h-[580px] p-4 rounded shadow-lg">
        {/* Title */}
        <h1 className="text-center text-[#002B53] text-3xl font-bold mt-12">Registration</h1>

        {/* Form Sections */}
        <div className="flex mt-8">
          {/* Column 1 */}
          <div className="w-1/2 px-8">
            {/* First Name */}
            <div className="mb-4">
              <label className="text-[#002B53] font-bold">First Name:</label>
              <input
                type="text"
                name="fname"
                value={formData.fname}
                onChange={handleChange}
                className="w-full mt-1 p-2 rounded border border-gray-300"
                style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
              />
            </div>

            {/* Last Name */}
            <div className="mb-4">
              <label className="text-[#002B53] font-bold">Last Name:</label>
              <input
                type="text"
                name="lname"
                value={formData.lname}
                onChange={handleChange}
                className="w-full mt-1 p-2 rounded border border-gray-300"
                style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
              />
            </div>

            {/* Security Question */}
            <div className="mb-4">
              <label className="text-[#002B53] font-bold">Select Security Question:</label>
              <select
                name="ssq"
                value={formData.ssq}
                onChange={handleChange}
                className="w-full mt-1 p-2 rounded border border-gray-300"
                style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
              >
                <option>Select</option>
                <option>Your Date of Birth</option>
                <option>Your Nick Name</option>
                <option>Your Favorite Book</option>
              </select>
            </div>

            {/* Security Answer */}
            <div className="mb-4">
              <label className="text-[#002B53] font-bold">Security Answer:</label>
              <input
                type="text"
                name="sa"
                value={formData.sa}
                onChange={handleChange}
                className="w-full mt-1 p-2 rounded border border-gray-300"
                style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
              />
            </div>
          </div>

          {/* Column 2 */}
          <div className="w-1/2 px-8">
            {/* Contact Number */}
            <div className="mb-4">
              <label className="text-[#002B53] font-bold">Contact No:</label>
              <input
                type="text"
                name="cnum"
                value={formData.cnum}
                onChange={handleChange}
                className="w-full mt-1 p-2 rounded border border-gray-300"
                style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
              />
            </div>

            {/* Email */}
            <div className="mb-4">
              <label className="text-[#002B53] font-bold">Email:</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="w-full mt-1 p-2 rounded border border-gray-300"
                style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
              />
            </div>

            {/* Password */}
            <div className="mb-4">
              <label className="text-[#002B53] font-bold">Password:</label>
              <input
                type="password"
                name="pwd"
                value={formData.pwd}
                onChange={handleChange}
                className="w-full mt-1 p-2 rounded border border-gray-300"
                style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
              />
            </div>

            {/* Confirm Password */}
            <div className="mb-4">
              <label className="text-[#002B53] font-bold">Confirm Password:</label>
              <input
                type="password"
                name="cpwd"
                value={formData.cpwd}
                onChange={handleChange}
                className="w-full mt-1 p-2 rounded border border-gray-300"
                style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
              />
            </div>
          </div>
        </div>

        {/* Checkbox */}
        <div className="flex items-center px-8 mt-4">
          <input
            type="checkbox"
            name="check"
            checked={formData.check}
            onChange={handleChange}
            className="mr-2"
          />
          <label className="text-[#002B53] font-bold">I Agree to the Terms & Conditions</label>
        </div>

        {/* Buttons */}
        <div className="flex justify-between px-8 mt-4">
          <button
            onClick={handleRegister}
            className="w-[270px] bg-[#002B53] text-white font-bold py-2 rounded hover:bg-[#007ACC]"
          >
            Register
          </button>
          <button
            onClick={handleSwitchToLogin}
            className="w-[270px] bg-[#002B53] text-white font-bold py-2 rounded hover:bg-[#007ACC]"
          >
            Login
          </button>
        </div>
      </div>
    </div>
  );
}

export default Register;
