import React, { useState, useEffect, useRef } from 'react';

function StudentPanel() {
  // State for form fields
  const [formData, setFormData] = useState({
    student_id: '',
    name: '',
    department: 'Select Department',
    course: 'Select Course',
    year: 'Select Year',
    semester: 'Select Semester',
    division: 'A',
    roll_no: '',
    gender: 'Male',
    dob: '',
    email: '',
    phone: '',
    address: '',
    teacher: '',
    photo_sample: 'No',
  });

  const [students, setStudents] = useState([]);
  const [searchRollNo, setSearchRollNo] = useState('');
  const videoRef = useRef(null);
  const [stream, setStream] = useState(null);

  // Fetch all students on component mount
  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    const response = await fetch('http://localhost:5000/api/students');
    const result = await response.json();
    if (result.status === 200) {
      setStudents(result.data);
    } else {
      alert(result.message);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleRadioChange = (value) => {
    setFormData({ ...formData, photo_sample: value });
  };

  const handleAdd = async () => {
    if (
      formData.department === 'Select Department' ||
      formData.course === 'Select Course' ||
      formData.year === 'Select Year' ||
      formData.semester === 'Select Semester' ||
      !formData.student_id ||
      !formData.name ||
      !formData.division ||
      !formData.roll_no ||
      !formData.gender ||
      !formData.dob ||
      !formData.email ||
      !formData.phone ||
      !formData.address ||
      !formData.teacher
    ) {
      alert('Please fill all fields!');
      return;
    }

    const response = await fetch('http://localhost:5000/api/students', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    });
    const result = await response.json();
    alert(result.message);
    if (result.status === 200) {
      fetchStudents();
      handleReset();
    }
  };

  const handleUpdate = async () => {
    if (
      formData.department === 'Select Department' ||
      formData.course === 'Select Course' ||
      formData.year === 'Select Year' ||
      formData.semester === 'Select Semester' ||
      !formData.student_id ||
      !formData.name ||
      !formData.division ||
      !formData.roll_no ||
      !formData.gender ||
      !formData.dob ||
      !formData.email ||
      !formData.phone ||
      !formData.address ||
      !formData.teacher
    ) {
      alert('Please fill all fields!');
      return;
    }

    if (window.confirm('Do you want to update this student?')) {
      const response = await fetch(`http://localhost:5000/api/students/${formData.student_id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      const result = await response.json();
      alert(result.message);
      if (result.status === 200) {
        fetchStudents();
        handleReset();
      }
    }
  };

  const handleDelete = async () => {
    if (!formData.student_id) {
      alert('Student ID is required!');
      return;
    }

    if (window.confirm('Do you want to delete this student?')) {
      const response = await fetch(`http://localhost:5000/api/students/${formData.student_id}`, {
        method: 'DELETE',
      });
      const result = await response.json();
      alert(result.message);
      if (result.status === 200) {
        fetchStudents();
        handleReset();
      }
    }
  };

  const handleReset = () => {
    setFormData({
      student_id: '',
      name: '',
      department: 'Select Department',
      course: 'Select Course',
      year: 'Select Year',
      semester: 'Select Semester',
      division: 'A',
      roll_no: '',
      gender: 'Male',
      dob: '',
      email: '',
      phone: '',
      address: '',
      teacher: '',
      photo_sample: 'No',
    });
  };

  const handleSearch = async () => {
    if (!searchRollNo) {
      alert('Please enter a roll number to search!');
      return;
    }

    const response = await fetch(`http://localhost:5000/api/students/search?roll_no=${searchRollNo}`);
    const result = await response.json();
    if (result.status === 200) {
      setStudents(result.data);
    } else {
      alert(result.message);
    }
  };

  const handleRowClick = (student) => {
    setFormData({
      student_id: student.Student_ID,
      name: student.Name,
      department: student.Department,
      course: student.Course,
      year: student.Year,
      semester: student.Semester,
      division: student.Division,
      roll_no: student.Roll_No,
      gender: student.Gender,
      dob: student.DOB,
      email: student.Email,
      phone: student.Phone_No,
      address: student.Address,
      teacher: student.Teacher_Name,
      photo_sample: student.PhotoSample,
    });
  };

  const startVideo = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      setStream(stream);
    } catch (err) {
      alert('Error accessing webcam: ' + err.message);
    }
  };

  const stopVideo = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
  };

  const capturePhoto = async () => {
    if (!formData.student_id) {
      alert('Please enter a student ID before capturing a photo!');
      return;
    }

    const canvas = document.createElement('canvas');
    canvas.width = videoRef.current.videoWidth;
    canvas.height = videoRef.current.videoHeight;
    const ctx = canvas.getContext('2d');
    ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    const formDataToSend = new FormData();
    canvas.toBlob(async (blob) => {
      formDataToSend.append('image', blob, `student.${formData.student_id}.jpg`);

      const response = await fetch(`http://localhost:5000/api/students/${formData.student_id}/photo`, {
        method: 'POST',
        body: formDataToSend,
      });
      const result = await response.json();
      alert(result.message);
      if (result.status === 200) {
        setFormData({ ...formData, photo_sample: 'Yes' });
        fetchStudents();
      }
    }, 'image/jpeg');

    stopVideo();
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-3xl font-bold text-center text-blue-900 mb-4">Welcome to Student Panel</h1>
      <div className="bg-white shadow-md rounded p-4 flex flex-col lg:flex-row gap-4">
        {/* Left Frame: Student Details Form */}
        <div className="w-full lg:w-1/2 border p-4 rounded">
          <h2 className="text-xl font-bold text-blue-900 mb-4">Student Details</h2>

          {/* Current Course Section */}
          <div className="border p-4 mb-4">
            <h3 className="text-lg font-semibold text-blue-900 mb-2">Current Course</h3>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-blue-900 font-semibold">Department</label>
                <select
                  name="department"
                  value={formData.department}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                >
                  <option>Select Department</option>
                  <option>Computer</option>
                  <option>IT</option>
                  <option>Civil</option>
                  <option>Mechanical</option>
                  <option>Electrical</option>
                </select>
              </div>
              <div>
                <label className="block text-blue-900 font-semibold">Course</label>
                <select
                  name="course"
                  value={formData.course}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                >
                  <option>Select Course</option>
                  <option>SE</option>
                  <option>FE</option>
                  <option>TE</option>
                  <option>BE</option>
                  <option>MS</option>
                </select>
              </div>
              <div>
                <label className="block text-blue-900 font-semibold">Year</label>
                <select
                  name="year"
                  value={formData.year}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                >
                  <option>Select Year</option>
                  <option>2017-21</option>
                  <option>2018-22</option>
                  <option>2019-23</option>
                  <option>2020-24</option>
                  <option>2021-25</option>
                </select>
              </div>
              <div>
                <label className="block text-blue-900 font-semibold">Semester</label>
                <select
                  name="semester"
                  value={formData.semester}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                >
                  <option>Select Semester</option>
                  <option>Semester-1</option>
                  <option>Semester-2</option>
                  <option>Semester-3</option>
                  <option>Semester-4</option>
                  <option>Semester-5</option>
                  <option>Semester-6</option>
                  <option>Semester-7</option>
                  <option>Semester-8</option>
                </select>
              </div>
            </div>
          </div>

          {/* Class Student Information Section */}
          <div className="border p-4 mb-4">
            <h3 className="text-lg font-semibold text-blue-900 mb-2">Class Student Information</h3>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-blue-900 font-semibold">Student ID</label>
                <input
                  type="text"
                  name="student_id"
                  value={formData.student_id}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                />
              </div>
              <div>
                <label className="block text-blue-900 font-semibold">Student Name</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                />
              </div>
              <div>
                <label className="block text-blue-900 font-semibold">Class Division</label>
                <select
                  name="division"
                  value={formData.division}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                >
                  <option>A</option>
                  <option>B</option>
                  <option>C</option>
                  <option>D</option>
                  <option>E</option>
                  <option>F</option>
                </select>
              </div>
              <div>
                <label className="block text-blue-900 font-semibold">Roll No</label>
                <input
                  type="text"
                  name="roll_no"
                  value={formData.roll_no}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                />
              </div>
              <div>
                <label className="block text-blue-900 font-semibold">Gender</label>
                <select
                  name="gender"
                  value={formData.gender}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                >
                  <option>Male</option>
                  <option>Female</option>
                  <option>Others</option>
                </select>
              </div>
              <div>
                <label className="block text-blue-900 font-semibold">DOB</label>
                <input
                  type="text"
                  name="dob"
                  value={formData.dob}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                />
              </div>
              <div>
                <label className="block text-blue-900 font-semibold">Email</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                />
              </div>
              <div>
                <label className="block text-blue-900 font-semibold">Phone</label>
                <input
                  type="text"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                />
              </div>
              <div>
                <label className="block text-blue-900 font-semibold">Address</label>
                <input
                  type="text"
                  name="address"
                  value={formData.address}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                />
              </div>
              <div>
                <label className="block text-blue-900 font-semibold">Teacher Name</label>
                <input
                  type="text"
                  name="teacher"
                  value={formData.teacher}
                  onChange={handleInputChange}
                  className="w-full p-2 border rounded"
                />
              </div>
            </div>
            <div className="mt-4">
              <label className="mr-4">
                <input
                  type="radio"
                  name="photo_sample"
                  value="Yes"
                  checked={formData.photo_sample === 'Yes'}
                  onChange={() => handleRadioChange('Yes')}
                />
                Take Photo Sample
              </label>
              <label>
                <input
                  type="radio"
                  name="photo_sample"
                  value="No"
                  checked={formData.photo_sample === 'No'}
                  onChange={() => handleRadioChange('No')}
                />
                No Photo Sample
              </label>
            </div>
          </div>

          {/* Buttons */}
          <div className="flex gap-2">
            <button onClick={handleAdd} className="p-2 bg-blue-900 text-white rounded">Save</button>
            <button onClick={handleUpdate} className="p-2 bg-blue-900 text-white rounded">Update</button>
            <button onClick={handleDelete} className="p-2 bg-blue-900 text-white rounded">Delete</button>
            <button onClick={handleReset} className="p-2 bg-blue-900 text-white rounded">Reset</button>
            <button onClick={startVideo} className="p-2 bg-blue-900 text-white rounded">Take Pic</button>
            <button onClick={capturePhoto} className="p-2 bg-blue-900 text-white rounded">Capture Pic</button>
            <button onClick={stopVideo} className="p-2 bg-blue-900 text-white rounded">Stop Pic</button>
          </div>

          {/* Video Feed */}
          {stream && (
            <div className="mt-4">
              <video ref={videoRef} autoPlay className="w-full max-w-md border rounded"></video>
            </div>
          )}
        </div>

        {/* Right Frame: Student Table */}
        <div className="w-full lg:w-1/2 border p-4 rounded">
          <h2 className="text-xl font-bold text-blue-900 mb-4">Student Details</h2>

          {/* Search Section */}
          <div className="border p-4 mb-4 flex gap-2">
            <label className="text-blue-900 font-semibold">Search by Roll No:</label>
            <input
              type="text"
              value={searchRollNo}
              onChange={(e) => setSearchRollNo(e.target.value)}
              className="p-2 border rounded"
            />
            <button onClick={handleSearch} className="p-2 bg-blue-900 text-white rounded">Search</button>
            <button onClick={fetchStudents} className="p-2 bg-blue-900 text-white rounded">Show All</button>
          </div>

          {/* Table */}
          <div className="overflow-x-auto">
            <table className="w-full border-collapse border">
              <thead>
                <tr className="bg-blue-900 text-white">
                  <th className="border p-2">Student ID</th>
                  <th className="border p-2">Name</th>
                  <th className="border p-2">Department</th>
                  <th className="border p-2">Course</th>
                  <th className="border p-2">Year</th>
                  <th className="border p-2">Semester</th>
                  <th className="border p-2">Division</th>
                  <th className="border p-2">Gender</th>
                  <th className="border p-2">DOB</th>
                  <th className="border p-2">Phone</th>
                  <th className="border p-2">Address</th>
                  <th className="border p-2">Roll No</th>
                  <th className="border p-2">Email</th>
                  <th className="border p-2">Teacher</th>
                  <th className="border p-2">Photo Sample</th>
                </tr>
              </thead>
              <tbody>
                {students.map((student) => (
                  <tr
                    key={student.Student_ID}
                    onClick={() => handleRowClick(student)}
                    className="cursor-pointer hover:bg-gray-200"
                  >
                    <td className="border p-2">{student.Student_ID}</td>
                    <td className="border p-2">{student.Name}</td>
                    <td className="border p-2">{student.Department}</td>
                    <td className="border p-2">{student.Course}</td>
                    <td className="border p-2">{student.Year}</td>
                    <td className="border p-2">{student.Semester}</td>
                    <td className="border p-2">{student.Division}</td>
                    <td className="border p-2">{student.Gender}</td>
                    <td className="border p-2">{student.DOB}</td>
                    <td className="border p-2">{student.Phone_No}</td>
                    <td className="border p-2">{student.Address}</td>
                    <td className="border p-2">{student.Roll_No}</td>
                    <td className="border p-2">{student.Email}</td>
                    <td className="border p-2">{student.Teacher_Name}</td>
                    <td className="border p-2">{student.PhotoSample}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default StudentPanel;
