import React from 'react';

function Train({ onBack }) {
  const trainClassifier = async () => {
    try {
      // Simulate calling a backend API to perform the training
      const response = await fetch('http://localhost:5000/api/train', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const result = await response.json();

      if (response.ok) {
        alert('Training Dataset Completed!');
      } else {
        alert(`Error: ${result.message || 'Training failed'}`);
      }
    } catch (error) {
      alert(`Error: ${error.message || 'Failed to connect to the server'}`);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <div className="relative">
        <img src="/images/banner1.jpg" alt="Banner" className="w-full h-32 object-cover" />
        <div className="absolute top-0 left-0 w-full h-full flex items-center justify-between px-4 bg-opacity-50 bg-black">
          <button onClick={onBack} className="bg-gray-500 text-white px-4 py-2 rounded">
            Back Home
          </button>
          <h1 className="text-3xl font-bold text-white">Welcome to Training Panel</h1>
          <div></div>
        </div>
      </div>

      {/* Main Content */}
      <div className="relative">
        <img src="/images/at.jpeg" alt="Background" className="w-full h-screen object-cover absolute top-0 left-0 z-0" />
        <div className="relative z-10 flex justify-center pt-20">
          <div className="flex flex-col items-center">
            <button onClick={trainClassifier} className="w-48 h-48 bg-gray-300 flex items-center justify-center">
              <img src="/images/t_btn1.png" alt="Train Dataset" className="w-full h-full object-cover" />
            </button>
            <button onClick={trainClassifier} className="mt-4 w-48 bg-white text-blue-900 font-bold py-2">
              Train Dataset
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Train;
