import React, { useState, useEffect } from 'react';

function Attendance({ onBack }) {
  const [formData, setFormData] = useState({
    id: '',
    roll_no: '',
    name: '',
    time: '',
    date: '',
    status: 'Status',
  });

  const [csvData, setCsvData] = useState([]);
  const [attendanceData, setAttendanceData] = useState([]);
  const [csvInput, setCsvInput] = useState('');

  // Fetch attendance data from MySQL on component mount
  useEffect(() => {
    fetchAttendance();
  }, []);

  const fetchAttendance = async () => {
    const response = await fetch('http://localhost:5000/api/attendance');
    const data = await response.json();
    if (data.status === 200) {
      setAttendanceData(data.attendance);
    } else {
      alert(data.message);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleReset = () => {
    setFormData({
      id: '',
      roll_no: '',
      name: '',
      time: '',
      date: '',
      status: 'Status',
    });
  };

  const handleCsvInputChange = (e) => {
    setCsvInput(e.target.value);
  };

  const handleImportCsv = async () => {
    if (!csvInput) {
      alert('Please enter CSV data to import!');
      return;
    }

    const rows = csvInput.split('\n').map(row => row.split(','));
    if (rows.length === 0) {
      alert('No valid CSV data provided!');
      return;
    }

    // Update left table (CSV data)
    setCsvData(rows);

    // Send CSV data to backend to save in MySQL
    const response = await fetch('http://localhost:5000/api/import-csv', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ csv_data: rows }),
    });
    const data = await response.json();
    alert(data.message);
    if (data.status === 200) {
      fetchAttendance(); // Refresh MySQL table
    }
  };

  const handleExportCsv = async () => {
    const response = await fetch('http://localhost:5000/api/export-csv');
    if (response.ok) {
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'attendance_export.csv';
      document.body.appendChild(a);
      a.click();
      a.remove();
      window.URL.revokeObjectURL(url);
      alert('CSV exported successfully!');
    } else {
      const data = await response.json();
      alert(data.message);
    }
  };

  const handleUpdateCsv = async () => {
    const requiredFields = ['id', 'roll_no', 'name', 'time', 'date', 'status'];
    if (requiredFields.some(field => !formData[field] || formData[field] === 'Status')) {
      alert('Please fill all fields!');
      return;
    }

    const response = await fetch('http://localhost:5000/api/attendance', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    });
    const data = await response.json();
    alert(data.message);
    if (data.status === 200) {
      fetchAttendance();
      handleReset();
    }
  };

  const handleUpdate = async () => {
    const requiredFields = ['id', 'roll_no', 'name', 'time', 'date', 'status'];
    if (requiredFields.some(field => !formData[field] || formData[field] === 'Status')) {
      alert('Please fill all fields!');
      return;
    }

    const response = await fetch(`http://localhost:5000/api/attendance/${formData.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    });
    const data = await response.json();
    alert(data.message);
    if (data.status === 200) {
      fetchAttendance();
      handleReset();
    }
  };

  const handleDelete = async () => {
    if (!formData.id) {
      alert('Please select an attendance record to delete!');
      return;
    }

    if (!window.confirm('Do you want to delete this attendance record?')) return;

    const response = await fetch(`http://localhost:5000/api/attendance/${formData.id}`, {
      method: 'DELETE',
    });
    const data = await response.json();
    alert(data.message);
    if (data.status === 200) {
      fetchAttendance();
      handleReset();
    }
  };

  const handleCsvRowClick = (row) => {
    setFormData({
      id: row[0],
      roll_no: row[1],
      name: row[2],
      time: row[3],
      date: row[4],
      status: row[5],
    });
  };

  const handleAttendanceRowClick = (row) => {
    setFormData({
      id: row.id,
      roll_no: row.roll_no,
      name: row.name,
      time: row.time,
      date: row.date,
      status: row.status,
    });
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      {/* Header */}
      <div className="bg-blue-900 text-white p-4 flex justify-between items-center">
        <button onClick={onBack} className="bg-gray-500 text-white px-4 py-2 rounded">
          Back Home
        </button>
        <h1 className="text-3xl font-bold">Welcome to Attendance Panel</h1>
        <div></div>
      </div>

      <div className="bg-white p-6 rounded shadow mt-4">
        <div className="flex">
          {/* Left Section: Form and CSV Table */}
          <div className="w-1/2 pr-4 border-r">
            <h2 className="text-xl font-bold text-blue-900 mb-4">Student Details</h2>

            {/* Form */}
            <div className="border p-4 mb-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-blue-900 font-bold">Std-ID</label>
                  <input
                    type="text"
                    name="id"
                    value={formData.id}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="block text-blue-900 font-bold">Roll.No</label>
                  <input
                    type="text"
                    name="roll_no"
                    value={formData.roll_no}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="block text-blue-900 font-bold">Std-Name</label>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="block text-blue-900 font-bold">Time</label>
                  <input
                    type="text"
                    name="time"
                    value={formData.time}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="block text-blue-900 font-bold">Date</label>
                  <input
                    type="text"
                    name="date"
                    value={formData.date}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="block text-blue-900 font-bold">Attend-status</label>
                  <select
                    name="status"
                    value={formData.status}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded"
                  >
                    <option>Status</option>
                    <option>Present</option>
                    <option>Absent</option>
                  </select>
                </div>
              </div>
            </div>

            {/* CSV Table */}
            <div className="border p-4 max-h-80 overflow-auto">
              <table className="w-full border-collapse">
                <thead>
                  <tr className="bg-blue-900 text-white">
                    <th className="border p-2">Std-ID</th>
                    <th className="border p-2">Roll.No</th>
                    <th className="border p-2">Std-Name</th>
                    <th className="border p-2">Time</th>
                    <th className="border p-2">Date</th>
                    <th className="border p-2">Attend-status</th>
                  </tr>
                </thead>
                <tbody>
                  {csvData.map((row, index) => (
                    <tr
                      key={index}
                      onClick={() => handleCsvRowClick(row)}
                      className="cursor-pointer hover:bg-gray-100"
                    >
                      <td className="border p-2">{row[0]}</td>
                      <td className="border p-2">{row[1]}</td>
                      <td className="border p-2">{row[2]}</td>
                      <td className="border p-2">{row[3]}</td>
                      <td className="border p-2">{row[4]}</td>
                      <td className="border p-2">{row[5]}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* Buttons */}
            <div className="mt-4 flex space-x-2">
              <button onClick={handleImportCsv} className="bg-blue-900 text-white px-4 py-2 rounded">
                Import CSV
              </button>
              <button onClick={handleExportCsv} className="bg-blue-900 text-white px-4 py-2 rounded">
                Export CSV
              </button>
              <button onClick={handleUpdateCsv} className="bg-blue-900 text-white px-4 py-2 rounded">
                Update
              </button>
              <button onClick={handleReset} className="bg-blue-900 text-white px-4 py-2 rounded">
                Reset
              </button>
            </div>
          </div>

          {/* Right Section: MySQL Table */}
          <div className="w-1/2 pl-4">
            <h2 className="text-xl font-bold text-blue-900 mb-4">Student Details</h2>

            {/* CSV Input for Import */}
            <div className="border p-4 mb-4">
              <h3 className="text-lg font-bold text-blue-900 mb-2">Paste CSV Data</h3>
              <textarea
                value={csvInput}
                onChange={handleCsvInputChange}
                className="w-full p-2 border rounded"
                rows="3"
                placeholder="Paste CSV data here (e.g., ID,Roll.No,Name,Time,Date,Status)"
              />
            </div>

            {/* MySQL Table */}
            <div className="border p-4 max-h-96 overflow-auto">
              <table className="w-full border-collapse">
                <thead>
                  <tr className="bg-blue-900 text-white">
                    <th className="border p-2">Std-ID</th>
                    <th className="border p-2">Roll.No</th>
                    <th className="border p-2">Std-Name</th>
                    <th className="border p-2">Time</th>
                    <th className="border p-2">Date</th>
                    <th className="border p-2">Attend-status</th>
                  </tr>
                </thead>
                <tbody>
                  {attendanceData.map((row) => (
                    <tr
                      key={row.id}
                      onClick={() => handleAttendanceRowClick(row)}
                      className="cursor-pointer hover:bg-gray-100"
                    >
                      <td className="border p-2">{row.id}</td>
                      <td className="border p-2">{row.roll_no}</td>
                      <td className="border p-2">{row.name}</td>
                      <td className="border p-2">{row.time}</td>
                      <td className="border p-2">{row.date}</td>
                      <td className="border p-2">{row.status}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* Buttons */}
            <div className="mt-4 flex space-x-2">
              <button onClick={handleUpdate} className="bg-blue-900 text-white px-4 py-2 rounded">
                Update
              </button>
              <button onClick={handleDelete} className="bg-blue-900 text-white px-4 py-2 rounded">
                Delete
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Attendance;
