import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showForgetModal, setShowForgetModal] = useState(false);
  const [securityQuestion, setSecurityQuestion] = useState('Select');
  const [securityAnswer, setSecurityAnswer] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [message, setMessage] = useState('');

  const togglePassword = () => {
    setShowPassword(!showPassword);
  };

  const handleLogin = async () => {
    if (!username || !password) {
      setMessage('All Fields Required!');
      return;
    }

    if (username === 'admin' && password === 'admin') {
      setMessage('Welcome to Attendance Management System Using Facial Recognition');
      // Set admin credentials in localStorage
      localStorage.setItem('username', 'Admin User');
      localStorage.setItem('email', 'admin@example.com');
      localStorage.setItem('loggedIn', 'true');
      window.dispatchEvent(new Event('storageChange'));
      navigate('/');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: username, password }),
      });

      const result = await response.json();

      // Debug: Log the API response to inspect its structure
      console.log('Login API Response:', result);

      if (response.ok) {
        // Fetch user details after successful login
        let fname = '';
        let lname = '';
        let email = username;
        let pwd = password;

        try {
          const userResponse = await fetch(`http://localhost:5000/api/user?email=${encodeURIComponent(username)}`, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
            },
          });

          const userResult = await userResponse.json();
          console.log('User API Response:', userResult);

          if (userResponse.ok) {
            const userData = userResult.user || userResult;
            fname = userData.fname || userData.firstName || userData.first_name || userData.given_name || userData.name?.split(' ')[0] || '';
            lname = userData.lname || userData.lastName || userData.last_name || userData.family_name || userData.name?.split(' ')[1] || '';
            email = userData.email || username;
            pwd = userData.pwd || userData.password || userData.pass || password;
          }
        } catch (error) {
          console.log('Error fetching user details:', error.message);
        }

        // Use the existing username from localStorage (set by Register.js) as a fallback
        const storedUsername = localStorage.getItem('username');
        const displayName = fname
          ? `${fname} ${lname}`.trim()
          : storedUsername || email.split('@')[0].replace(/[^a-zA-Z\s]/g, ' ').trim() || 'User';

        // Store credentials in localStorage
        localStorage.setItem('username', displayName);
        localStorage.setItem('email', email);
        localStorage.setItem('password', pwd); // Still storing for consistency, but won't display in Settings
        localStorage.setItem('loggedIn', 'true');

        // Debug: Log the values being stored in localStorage
        console.log('Storing in localStorage:', {
          username: displayName,
          email: email,
          password: pwd,
        });

        window.dispatchEvent(new Event('storageChange'));
        setMessage('Login Successful\nATTEND');
        navigate('/');
      } else {
        setMessage(result.message || 'Invalid Username or Password!');
      }
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }
  };

  const handleRegister = () => {
    navigate('/register');
  };

  const handleForgetPassword = async () => {
    if (!username) {
      setMessage('Please Enter the Email ID to reset Password!');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/check-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: username }),
      });

      const result = await response.json();

      if (response.ok && result.exists) {
        setShowForgetModal(true);
      } else {
        setMessage('Please Enter the Valid Email ID!');
      }
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }
  };

  const handleResetPassword = async () => {
    if (securityQuestion === 'Select') {
      setMessage('Select the Security Question!');
      return;
    }
    if (!securityAnswer) {
      setMessage('Please Enter the Answer!');
      return;
    }
    if (!newPassword) {
      setMessage('Please Enter the New Password!');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/reset-password', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: username,
          securityQuestion,
          securityAnswer,
          newPassword,
        }),
      });

      const result = await response.json();

      if (response.ok) {
        // Update password in localStorage
        localStorage.setItem('password', newPassword);
        setMessage('Successfully Your password has been reset, Please login with new Password!');
        setShowForgetModal(false);
        setSecurityQuestion('Select');
        setSecurityAnswer('');
        setNewPassword('');
      } else {
        setMessage('Please Enter the Correct Answer!');
      }
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleLogin();
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 relative">
      {/* Background Image */}
      <img src="/images/login.jpg" alt="Background" className="absolute inset-0 w-full h-full object-cover" />

      {/* Login Frame */}
      <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-[#002B53] w-[340px] h-[450px] p-4 rounded shadow-lg flex flex-col">
        {/* Header Image */}
        <div className="flex justify-center">
          <img src="/images/log1.png" alt="Login Icon" className="w-[100px] h-[100px]" />
        </div>

        {/* Title */}
        <h1 className="text-center text-white text-2xl font-bold mt-2">Login</h1>

        {/* Message Display */}
        {message && (
          <div className="mt-2 text-center text-white bg-red-500 p-2 rounded text-sm">
            {message}
          </div>
        )}

        {/* Form Fields */}
        <div className="flex-1 mt-4">
          {/* Username Field */}
          <div>
            <label className="text-white font-bold">Username:</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              onKeyPress={handleKeyPress}
              className="w-full mt-1 p-2 rounded border border-gray-300"
              style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
            />
          </div>

          {/* Password Field */}
          <div className="mt-4 relative">
            <label className="text-white font-bold">Password:</label>
            <input
              type={showPassword ? 'text' : 'password'}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              onKeyPress={handleKeyPress}
              className="w-full mt-1 p-2 rounded border border-gray-300"
              style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
            />
            <span
              onClick={togglePassword}
              className="absolute right-2 top-9 cursor-pointer text-lg"
            >
              {showPassword ? '👁‍🗨' : '👁'}
            </span>
          </div>
        </div>

        {/* Buttons */}
        <div className="mt-6">
          <button
            onClick={handleLogin}
            className="w-full bg-white text-[#002B53] font-bold py-2 rounded hover:bg-[#007ACC] hover:text-white"
          >
            Login
          </button>
          <div className="mt-4 flex justify-between">
            <button
              onClick={handleRegister}
              className="text-white font-bold hover:text-orange-500"
            >
              Register
            </button>
            <button
              onClick={handleForgetPassword}
              className="text-white font-bold hover:text-orange-500"
            >
              Forget
            </button>
          </div>
        </div>
      </div>

      {/* Forget Password Modal */}
      {showForgetModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded shadow-lg w-[400px]">
            <h2 className="text-center text-[#002B53] text-2xl font-bold">Forget Password</h2>

            {/* Security Question */}
            <div className="mt-4">
              <label className="text-[#002B53] font-bold">Select Security Question:</label>
              <select
                value={securityQuestion}
                onChange={(e) => setSecurityQuestion(e.target.value)}
                className="w-full mt-1 p-2 rounded border border-gray-300"
                style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
              >
                <option>Select</option>
                <option>Your Date of Birth</option>
                <option>Your Nick Name</option>
                <option>Your Favorite Book</option>
              </select>
            </div>

            {/* Security Answer */}
            <div className="mt-4">
              <label className="text-[#002B53] font-bold">Security Answer:</label>
              <input
                type="text"
                value={securityAnswer}
                onChange={(e) => setSecurityAnswer(e.target.value)}
                className="w-full mt-1 p-2 rounded border border-gray-300"
                style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
              />
            </div>

            {/* New Password */}
            <div className="mt-4">
              <label className="text-[#002B53] font-bold">New Password:</label>
              <input
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                className="w-full mt-1 p-2 rounded border border-gray-300"
                style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
              />
            </div>

            {/* Reset Password Button */}
            <div className="mt-6">
              <button
                onClick={handleResetPassword}
                className="w-full bg-[#002B53] text-white font-bold py-2 rounded hover:bg-[#007ACC]"
              >
                Reset Password
              </button>
            </div>

            {/* Close Modal Button */}
            <div className="mt-4 text-center">
              <button
                onClick={() => setShowForgetModal(false)}
                className="text-[#002B53] font-bold hover:text-red-500"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Login;
