import React, { useState } from 'react';
import ChatBot from './ChatBot'; // Assuming ChatBot.js is in the same directory

function App() {
  const [isChatOpen, setIsChatOpen] = useState(false);

  const handleChatBot = () => {
    setIsChatOpen((prev) => !prev); // Toggle chat visibility
  };

  const handleBack = () => {
    setIsChatOpen(false); // Close the chat when "Back Home" is clicked
  };

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Main page content */}
      <div className="p-4">
        <h1 className="text-3xl font-bold">Main Page</h1>
        <p className="mt-2">This is the main content of your page.</p>
      </div>

      {/* Bot Icon */}
      <div className="absolute bottom-4 right-4 flex flex-col items-end">
        <button
          onClick={handleChatBot}
          className="relative w-16 h-16 bg-blue-500 text-white flex items-center justify-center rounded-lg shadow-lg hover:bg-blue-600 transition-colors"
        >
          <img
            src="/images/chatbot.jpg"
            alt="ChatBot"
            className="w-10 h-10 object-cover"
          />
          <span
            className="absolute bottom-0 right-0 transform translate-x-1/4 translate-y-1/2 w-0 h-0 border-t-8 border-t-blue-500 border-l-8 border-l-transparent border-r-8 border-r-transparent"
          ></span>
        </button>
      </div>

      {/* ChatBot Window (conditionally rendered) */}
      {isChatOpen && (
        <div className="absolute bottom-20 right-4 w-full max-w-md bg-white shadow-lg rounded-lg overflow-hidden">
          <ChatBot onBack={handleBack} />
        </div>
      )}
    </div>
  );
}

export default App;
