import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import './App.css';
import Student from './components/Student';
import FaceDetector from './components/FaceDetector';
import Login from './components/Login';
import Register from './components/Register';
import Attendance from './components/Attendance';
import Dashboard from './components/Dashboard';
import Developers from './components/Developers';
import Goodbye from './components/Goodbye';
import ChatBot from './components/ChatBot';
import Train from './components/Train';
import LoadingScreen from './components/LoadingScreen'; // Import the new component

function MainPage() {
  const navigate = useNavigate();
  const [time, setTime] = useState(new Date().toLocaleTimeString());
  const [isChatOpen, setIsChatOpen] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => {
      setTime(new Date().toLocaleTimeString());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const handleStudentPanel = () => {
    navigate('/student');
  };

  const handleFaceDetector = () => {
    navigate('/face-detector');
  };

  const handleAttendance = () => {
    navigate('/attendance');
  };

  const handleHelpSupport = () => {
    navigate('/dashboard');
  };

  const handleDataTrain = () => {
    navigate('/train');
  };

  const handlePhotos = () => {
    alert('Opening Photos...');
  };

  const handleDevelopers = () => {
    navigate('/developers');
  };

  const handleExit = () => {
    if (window.confirm('Are you sure you want to exit?')) {
      navigate('/goodbye');
    }
  };

  const handleChatBot = () => {
    setIsChatOpen((prev) => !prev);
  };

  const handleChatBotClose = () => {
    setIsChatOpen(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('loggedIn');
    window.dispatchEvent(new Event('storageChange'));
    navigate('/login');
  };

  return (
    <div
      className="min-h-screen bg-gray-100 relative"
      style={{
        backgroundImage: `url('/images/bg.png')`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
      }}
    >
      <div className="bg-blue-900 text-white flex items-center justify-between p-2">
        <div className="bg-black text-white px-4 py-2">{time}</div>
        <h1 className="text-3xl font-bold text-center flex-1">
          Attendance Management System Using Facial Recognition
        </h1>
        <button
          onClick={handleLogout}
          className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
        >
          Logout
        </button>
      </div>

      <div className="relative p-8">
        <div className="grid grid-cols-4 gap-8 max-w-6xl mx-auto">
          <div className="flex flex-col items-center">
            <button onClick={handleStudentPanel} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/std1.jpg" alt="Student Panel" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleStudentPanel} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Student Panel
            </button>
          </div>

          <div className="flex flex-col items-center">
            <button onClick={handleFaceDetector} className="w-56 h-56 bg-gray-500 flex items-center justify-center">
              <img src="/images/det1.jpg" alt="Face Detector" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleFaceDetector} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Face Detector
            </button>
          </div>

          <div className="flex flex-col items-center">
            <button onClick={handleAttendance} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/att.jpg" alt="Attendance" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleAttendance} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Attendance
            </button>
          </div>

          <div className="flex flex-col items-center">
            <button onClick={handleHelpSupport} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/hlp.jpg" alt="Help Support" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleHelpSupport} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Help Support
            </button>
          </div>

          <div className="flex flex-col items-center">
            <button onClick={handleDataTrain} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/tra1.jpg" alt="Data Train" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleDataTrain} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Data Train
            </button>
          </div>

          <div className="flex flex-col items-center">
            <button onClick={handlePhotos} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/people.png" alt="Photos" className="w-full h-full object-cover" />
            </button>
            <button onClick={handlePhotos} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Photos
            </button>
          </div>

          <div className="flex flex-col items-center">
            <button onClick={handleDevelopers} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/develop.jpg" alt="Developers" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleDevelopers} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Developers
            </button>
          </div>

          <div className="flex flex-col items-center">
            <button onClick={handleExit} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/exit1.jpg" alt="Exit" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleExit} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Exit
            </button>
          </div>
        </div>

        {/* Bot Icon */}
        <div className="fixed bottom-4 right-4 flex flex-col items-end z-50">
          <button
            onClick={handleChatBot}
            className="relative w-14 h-14 bg-blue-500 text-white flex items-center justify-center rounded-full shadow-lg hover:bg-blue-600 transition-colors"
          >
            <img
              src="/images/chatbot.jpg"
              alt="ChatBot"
              className="w-8 h-8 rounded-full object-cover"
            />
            <span
              className="absolute bottom-0 right-0 transform translate-x-1/4 translate-y-1/4 w-0 h-0 border-t-6 border-t-blue-500 border-l-6 border-l-transparent border-r-6 border-r-transparent"
            ></span>
          </button>
        </div>

        {/* ChatBot Sliding Panel */}
        <div
          className={`fixed top-0 right-0 h-full w-full max-w-md transform transition-transform duration-300 z-50 ${
            isChatOpen ? 'translate-x-0' : 'translate-x-full'
          }`}
        >
          <ChatBot onBack={handleChatBotClose} />
        </div>

        {/* Backdrop */}
        {isChatOpen && (
          <div
            className="fixed inset-0 bg-black opacity-30 z-40"
            onClick={handleChatBotClose}
          ></div>
        )}
      </div>
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(localStorage.getItem('loggedIn') === 'true');
  const [showLoading, setShowLoading] = useState(false); // State to control loading screen

  useEffect(() => {
    const handleStorageChange = () => {
      const loggedIn = localStorage.getItem('loggedIn') === 'true';
      setIsLoggedIn(loggedIn);
      if (loggedIn && !showLoading) {
        setShowLoading(true); // Show loading screen after login
      }
    };

    window.addEventListener('storageChange', handleStorageChange);

    return () => {
      window.removeEventListener('storageChange', handleStorageChange);
    };
  }, [showLoading]);

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/" element={isLoggedIn ? (showLoading ? <LoadingScreen /> : <MainPage />) : <Login />} />
        <Route path="/loading" element={<LoadingScreen />} />
        <Route path="/register" element={<Register onBack={() => window.location.href = '/login'} />} />
        <Route path="/student" element={isLoggedIn ? <Student onBack={() => window.location.href = '/'} /> : <Login />} />
        <Route path="/face-detector" element={isLoggedIn ? <FaceDetector onBack={() => window.location.href = '/'} /> : <Login />} />
        <Route path="/attendance" element={isLoggedIn ? <Attendance onBack={() => window.location.href = '/'} /> : <Login />} />
        <Route path="/dashboard" element={isLoggedIn ? <Dashboard onBack={() => window.location.href = '/'} /> : <Login />} />
        <Route path="/developers" element={isLoggedIn ? <Developers onBack={() => window.location.href = '/'} /> : <Login />} />
        <Route path="/goodbye" element={<Goodbye />} />
        <Route path="/train" element={isLoggedIn ? <Train onBack={() => window.location.href = '/'} /> : <Login />} />
      </Routes>
    </Router>
  );
}

export default App;
