import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion'; // For animations
import { SunIcon, MoonIcon } from '@heroicons/react/24/solid'; // For theme toggle icons

function Train({ onBack }) {
  const [isTraining, setIsTraining] = useState(false);
  const [progress, setProgress] = useState(0);
  const [theme, setTheme] = useState('dark'); // Theme state: 'dark' or 'light'
  const [trainingStatus, setTrainingStatus] = useState(''); // Status: 'In Progress', 'Completed', etc.

  // Toggle between dark and light themes
  const toggleTheme = () => {
    setTheme(theme === 'dark' ? 'light' : 'dark');
  };

  // Play sound effect (requires an audio file and browser permissions)
  const playSound = (type) => {
    const soundMap = {
      click: '/sounds/click.mp3', // Replace with actual path to sound file
      success: '/sounds/success.mp3',
      error: '/sounds/error.mp3',
    };
    const audio = new Audio(soundMap[type]);
    audio.play().catch((error) => console.log('Sound playback failed:', error));
  };

  const trainClassifier = async () => {
    setIsTraining(true);
    setProgress(0);
    setTrainingStatus('In Progress');

    try {
      // Simulate progress updates
      const interval = setInterval(() => {
        setProgress((prev) => {
          if (prev >= 100) {
            clearInterval(interval);
            return 100;
          }
          return prev + 10;
        });
      }, 300);

      const response = await fetch('http://localhost:5000/api/train', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const result = await response.json();

      if (response.ok) {
        setTrainingStatus('Completed');
        playSound('success');
        alert('Training Dataset Completed!');
      } else {
        setTrainingStatus('Failed');
        playSound('error');
        alert(`Error: ${result.message || 'Training failed'}`);
      }
    } catch (error) {
      setTrainingStatus('Failed');
      playSound('error');
      alert(`Error: ${error.message || 'Failed to connect to the server'}`);
    } finally {
      setTimeout(() => {
        setIsTraining(false);
        setProgress(0);
        setTrainingStatus('');
      }, 1000);
    }
  };

  return (
    <div className={`min-h-screen relative overflow-hidden ${theme === 'dark' ? 'bg-gray-900' : 'bg-gray-100'} transition-colors duration-500`}>
      {/* Background Image with Overlay */}
      <div
        className="absolute inset-0 z-0"
        style={{
          backgroundImage: 'url(/images/at.jpeg)',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
          opacity: theme === 'dark' ? 0.3 : 0.5,
        }}
      />
      <div className={`absolute inset-0 ${theme === 'dark' ? 'bg-black bg-opacity-60' : 'bg-white bg-opacity-40'}`}></div>

      {/* Animated Particles */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {[...Array(20)].map((_, i) => (
          <motion.div
            key={i}
            className="particle"
            initial={{ opacity: 0, scale: 0 }}
            animate={{
              opacity: [0, 1, 0],
              scale: [0, 1, 0],
              x: Math.random() * 100 - 50,
              y: Math.random() * 100 - 50,
            }}
            transition={{
              duration: 5,
              repeat: Infinity,
              delay: Math.random() * 5,
              ease: 'easeInOut',
            }}
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
          ></motion.div>
        ))}
      </div>

      {/* Header */}
      <div className="relative z-10">
        <div className={`bg-gradient-to-r ${theme === 'dark' ? 'from-blue-800 to-indigo-600' : 'from-blue-500 to-indigo-400'} text-white p-4 flex justify-between items-center rounded-b-2xl shadow-lg`}>
          <motion.button
            onClick={() => {
              onBack();
              playSound('click');
            }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className={`flex items-center ${theme === 'dark' ? 'bg-gray-600' : 'bg-gray-300 text-gray-800'} px-4 py-2 rounded-lg hover:${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-400'} transition-all shadow-md`}
          >
            <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 19l-7-7 7-7"></path>
            </svg>
            Back Home
          </motion.button>
          <div className="flex items-center space-x-4">
            <img src="/images/logo.png" alt="Logo" className="w-10 h-10" /> {/* Branding Logo */}
            <h1 className="text-3xl font-bold font-['Poppins',_sans-serif] tracking-wide flex-1 text-center">
              Welcome to Training Panel
            </h1>
          </div>
          <motion.button
            onClick={() => {
              toggleTheme();
              playSound('click');
            }}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            className="p-2 rounded-full bg-gray-600 hover:bg-gray-700 transition-all"
          >
            {theme === 'dark' ? (
              <SunIcon className="w-6 h-6 text-yellow-400" />
            ) : (
              <MoonIcon className="w-6 h-6 text-gray-800" />
            )}
          </motion.button>
        </div>
      </div>

      {/* Main Content */}
      <div className="relative z-10 flex justify-center items-center min-h-[calc(100vh-80px)] px-4">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, ease: 'easeOut' }}
          className={`flex flex-col items-center ${theme === 'dark' ? 'bg-white/10 backdrop-blur-xl' : 'bg-white/80 backdrop-blur-md'} p-8 rounded-2xl shadow-xl w-full max-w-md border ${theme === 'dark' ? 'border-white/30' : 'border-gray-300/50'}`}
        >
          <div className="relative group">
            <motion.button
              onClick={() => {
                trainClassifier();
                playSound('click');
              }}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className={`w-48 h-48 ${theme === 'dark' ? 'bg-gray-800' : 'bg-gray-200'} flex items-center justify-center rounded-2xl border-4 ${theme === 'dark' ? 'border-blue-500/50' : 'border-blue-300/50'} hover:border-blue-500 transition-all duration-300 shadow-lg`}
              disabled={isTraining}
            >
              <motion.img
                src="/images/t_btn1.png"
                alt="Train Dataset"
                className={`w-full h-full object-cover rounded-2xl ${isTraining ? 'animate-pulse' : ''}`}
                animate={isTraining ? { scale: [1, 1.05, 1] } : {}}
                transition={{ duration: 1.5, repeat: Infinity, ease: 'easeInOut' }}
              />
            </motion.button>
            {/* Glowing Hover Effect */}
            <div className={`absolute inset-0 rounded-2xl shadow-[0_0_20px_rgba(59,130,246,0.5)] opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none ${theme === 'dark' ? 'shadow-blue-500' : 'shadow-blue-300'}`}></div>
            {/* Tooltip */}
            <motion.div
              className={`absolute top-0 left-1/2 transform -translate-x-1/2 -translate-y-12 ${theme === 'dark' ? 'bg-gray-800 text-white' : 'bg-gray-200 text-gray-800'} text-sm px-3 py-1 rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none`}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3 }}
            >
              Train the dataset for face recognition
            </motion.div>
          </div>
          <motion.button
            onClick={() => {
              trainClassifier();
              playSound('click');
            }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className={`mt-6 w-48 bg-gradient-to-r ${theme === 'dark' ? 'from-blue-600 to-indigo-600' : 'from-blue-400 to-indigo-400'} text-white font-bold py-3 rounded-xl hover:shadow-xl transition-all duration-300 shadow-md flex items-center justify-center font-['Poppins',_sans-serif]`}
            disabled={isTraining}
          >
            {isTraining ? (
              <svg className="w-5 h-5 mr-2 animate-spin" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 12a8 8 0 0116 0 8 8 0 01-16 0zm8-8a8 8 0 00-8 8"></path>
              </svg>
            ) : (
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 12H9m6 0a3 3 0 11-6 0 3 3 0 016 0z"></path>
              </svg>
            )}
            Train Dataset
          </motion.button>
          <AnimatePresence>
            {isTraining && (
              <motion.div
                initial={{ opacity: 0, height: 0 }}
                animate={{ opacity: 1, height: 'auto' }}
                exit={{ opacity: 0, height: 0 }}
                transition={{ duration: 0.3 }}
                className="mt-4 w-48"
              >
                <div className={`${theme === 'dark' ? 'bg-gray-700' : 'bg-gray-200'} rounded-full h-2.5`}>
                  <motion.div
                    className="bg-blue-500 h-2.5 rounded-full"
                    initial={{ width: '0%' }}
                    animate={{ width: `${progress}%` }}
                    transition={{ duration: 0.3 }}
                  ></motion.div>
                </div>
                <motion.p
                  className={`mt-2 text-sm font-['Poppins',_sans-serif] text-center ${theme === 'dark' ? 'text-blue-400' : 'text-blue-600'}`}
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ duration: 0.3 }}
                >
                  {trainingStatus}
                </motion.p>
              </motion.div>
            )}
          </AnimatePresence>
        </motion.div>
      </div>

      {/* CSS for Animations and Effects */}
      <style>
        {`
          @keyframes float {
            0%, 100% { transform: translateY(0); }
            50% { transform: translateY(-10px); }
          }
          .particle {
            position: absolute;
            width: 4px;
            height: 4px;
            background: ${theme === 'dark' ? 'rgba(59, 130, 246, 0.7)' : 'rgba(59, 130, 246, 0.4)'};
            border-radius: 50%;
            box-shadow: ${theme === 'dark' ? '0 0 10px rgba(59, 130, 246, 0.5)' : '0 0 5px rgba(59, 130, 246, 0.3)'};
          }
          @media (max-width: 640px) {
            .max-w-md {
              max-width: 90%;
            }
            h1 {
              font-size: 1.5rem;
            }
            .w-48 {
              width: 100%;
              max-width: 12rem;
            }
            .h-48 {
              height: 12rem;
            }
          }
        `}
      </style>
    </div>
  );
}

export default Train;
