import React, { useState } from 'react';

function FaceDetector({ onBack }) {
  const [isScanning, setIsScanning] = useState(false);

  const handleFaceRecognition = async () => {
    setIsScanning(true);
    try {
      // Clear session before starting new face recognition
      await fetch('http://localhost:5000/api/clear-session', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });

      // Simulate face recognition by sending a student_id
      const response = await fetch('http://localhost:5000/api/mark-attendance', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ student_id: '1' }),
      });
      const data = await response.json();
      alert(data.message);

      if (data.status === 200 && data.student) {
        const { id, name, roll, department } = data.student;
        alert(`Recognized Student:\nID: ${id}\nName: ${name}\nRoll: ${roll}\nDepartment: ${department}`);
      }
    } catch (error) {
      console.error('Error during face recognition:', error);
      alert('Failed to perform face recognition. Please check the console for details.');
    } finally {
      setTimeout(() => setIsScanning(false), 2000); // Reset scanning animation after 2 seconds
    }
  };

  return (
    <div
      className="min-h-screen relative overflow-hidden"
      style={{
        backgroundImage: 'url(/images/bb.jpg)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
      }}
    >
      {/* Dark Overlay */}
      <div className="absolute inset-0 bg-black bg-opacity-70"></div>

      {/* Animated Particles */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {[...Array(20)].map((_, i) => (
          <div
            key={i}
            className="particle"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 5}s`,
            }}
          ></div>
        ))}
      </div>

      {/* Content */}
      <div className="relative z-10">
        {/* Header */}
        <div className="bg-gradient-to-r from-blue-900 to-purple-900 text-white p-4 flex justify-between items-center rounded-b-2xl shadow-lg">
          <button onClick={onBack} className="flex items-center bg-gray-600 text-white px-4 py-2 rounded-lg hover:bg-gray-700 hover:scale-105 transition-all shadow-md">
            <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 19l-7-7 7-7"></path>
            </svg>
            Back Home
          </button>
          <h1 className="text-3xl font-bold font-['Montserrat',_sans-serif] tracking-wide text-transparent bg-clip-text bg-gradient-to-r from-blue-300 to-purple-300">
            Welcome to Face Recognition Panel
          </h1>
          <div className="w-12 h-12"></div>
        </div>
        <div className="relative flex justify-center">
          <div className="w-64 h-1 bg-gradient-to-r from-blue-500 to-purple-500 rounded animate-pulse mt-2"></div>
        </div>

        {/* Main Content */}
        <div className="flex justify-center items-center min-h-[calc(100vh-120px)] px-4">
          <div className="flex flex-col items-center">
            <div className="relative w-64 h-64 mb-6">
              <button
                onClick={handleFaceRecognition}
                className="w-full h-full bg-gray-800 flex items-center justify-center rounded-2xl border-4 border-transparent hover:border-blue-500 transition-all duration-300 shadow-lg"
              >
                <img
                  src="/images/f_det.jpg"
                  alt="Face Detector"
                  className={`w-full h-full object-cover rounded-2xl ${isScanning ? 'opacity-50' : ''}`}
                />
                {/* Scanning Animation */}
                {isScanning && (
                  <div className="absolute inset-0 flex items-center justify-center">
                    <div className="scan-line"></div>
                  </div>
                )}
              </button>
              {/* Neon Blue Glow Effect */}
              <div className="absolute inset-0 rounded-2xl shadow-[0_0_20px_rgba(59,130,246,0.5)] opacity-0 hover:opacity-100 transition-opacity duration-300 pointer-events-none"></div>
            </div>
            <button
              onClick={handleFaceRecognition}
              className="relative w-64 bg-gradient-to-r from-blue-600 to-purple-600 text-white font-bold py-3 rounded-xl hover:scale-105 hover:shadow-xl transition-all duration-300 shadow-md flex items-center justify-center"
            >
              <svg className="w-6 h-6 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 12h18M3 6h18M3 18h18M9 6v12M15 6v12"></path>
              </svg>
              Face Detector
            </button>
          </div>
        </div>
      </div>

      {/* CSS for Animations and Effects */}
      <style>
        {`
          @keyframes float {
            0%, 100% { transform: translateY(0); }
            50% { transform: translateY(-10px); }
          }
          @keyframes scan {
            0% { transform: translateY(-100%); }
            100% { transform: translateY(100%); }
          }
          .particle {
            position: absolute;
            width: 4px;
            height: 4px;
            background: rgba(59, 130, 246, 0.7);
            border-radius: 50%;
            animation: float 5s infinite ease-in-out;
            box-shadow: 0 0 10px rgba(59, 130, 246, 0.5);
          }
          .scan-line {
            position: absolute;
            width: 100%;
            height: 2px;
            background: linear-gradient(to right, transparent, #3b82f6, transparent);
            animation: scan 1s infinite linear;
          }
        `}
      </style>
    </div>
  );
}

export default FaceDetector;
