import React from 'react';

function Attendance() {
  const handleMarkAttendance = async () => {
    // Placeholder for capturing image and sending to backend
    alert('Attendance marking is not fully implemented yet.');
  };

  return (
    <div className="mb-4 p-4 bg-white rounded shadow">
      <h2 className="text-2xl mb-4">Mark Attendance</h2>
      <button onClick={handleMarkAttendance} className="w-full p-2 bg-yellow-500 text-white rounded">
        Mark Attendance
      </button>
    </div>
  );
}

export default Attendance;
