import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showForgetModal, setShowForgetModal] = useState(false);
  const [securityQuestion, setSecurityQuestion] = useState('Select');
  const [securityAnswer, setSecurityAnswer] = useState('');
  const [newPassword, setNewPassword] = useState('');

  const togglePassword = () => {
    setShowPassword(!showPassword);
  };

  const handleLogin = async () => {
    if (!username || !password) {
      alert('All Fields Required!');
      return;
    }

    if (username === 'admin' && password === 'admin') {
      alert('Welcome to Attendance Management System Using Facial Recognition');
      localStorage.setItem('loggedIn', 'true'); // Store login state
      navigate('/');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: username, password }),
      });

      const result = await response.json();

      if (response.ok) {
        alert('Login Successful\nATTEND');
        localStorage.setItem('loggedIn', 'true'); // Store login state
        navigate('/'); // Navigate immediately after alert
      } else {
        alert('Invalid Username or Password!');
      }
    } catch (error) {
      alert(`Database error: ${error.message}`);
    }
  };

  const handleRegister = () => {
    navigate('/register');
  };

  const handleForgetPassword = async () => {
    if (!username) {
      alert('Please Enter the Email ID to reset Password!');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/check-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: username }),
      });

      const result = await response.json();

      if (response.ok && result.exists) {
        setShowForgetModal(true);
      } else {
        alert('Please Enter the Valid Email ID!');
      }
    } catch (error) {
      alert(`Database error: ${error.message}`);
    }
  };

  const handleResetPassword = async () => {
    if (securityQuestion === 'Select') {
      alert('Select the Security Question!');
      return;
    }
    if (!securityAnswer) {
      alert('Please Enter the Answer!');
      return;
    }
    if (!newPassword) {
      alert('Please Enter the New Password!');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/reset-password', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: username,
          securityQuestion,
          securityAnswer,
          newPassword,
        }),
      });

      const result = await response.json();

      if (response.ok) {
        alert('Successfully Your password has been reset, Please login with new Password!');
        setShowForgetModal(false);
        setSecurityQuestion('Select');
        setSecurityAnswer('');
        setNewPassword('');
      } else {
        alert('Please Enter the Correct Answer!');
      }
    } catch (error) {
      alert(`Database error: ${error.message}`);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleLogin();
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 relative">
      {/* Background Image */}
      <img src="/images/login.jpg" alt="Background" className="absolute inset-0 w-full h-full object-cover" />

      {/* Login Frame */}
      <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-[#002B53] w-[340px] h-[450px] p-4 rounded shadow-lg">
        {/* Header Image */}
        <div className="flex justify-center">
          <img src="/images/log1.png" alt="Login Icon" className="w-[100px] h-[100px]" />
        </div>

        {/* Title */}
        <h1 className="text-center text-white text-2xl font-bold mt-2">Login</h1>

        {/* Username Field */}
        <div className="mt-4">
          <label className="text-white font-bold">Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            onKeyPress={handleKeyPress}
            className="w-full mt-1 p-2 rounded border border-gray-300"
            style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
          />
        </div>

        {/* Password Field */}
        <div className="mt-4 relative">
          <label className="text-white font-bold">Password:</label>
          <input
            type={showPassword ? 'text' : 'password'}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            onKeyPress={handleKeyPress}
            className="w-full mt-1 p-2 rounded border border-gray-300"
            style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
          />
          <span
            onClick={togglePassword}
            className="absolute right-2 top-9 cursor-pointer text-lg"
          >
            {showPassword ? 'üëÅ‚Äçüó®' : 'üëÅ'}
          </span>
        </div>

        {/* Buttons */}
        <div className="mt-6">
          <button
            onClick={handleLogin}
            className="w-full bg-white text-[#002B53] font-bold py-2 rounded hover:bg-[#007ACC] hover:text-white"
          >
            Login
          </button>
        </div>
        <div className="mt-4 flex justify-between">
          <button
            onClick={handleRegister}
            className="text-white font-bold hover:text-orange-500"
          >
            Register
          </button>
          <button
            onClick={handleForgetPassword}
            className="text-white font-bold hover:text-orange-500"
          >
            Forget
          </button>
        </div>
      </div>

      {/* Forget Password Modal */}
      {showForgetModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded shadow-lg w-[400px]">
            <h2 className="text-center text-[#002B53] text-2xl font-bold">Forget Password</h2>

            {/* Security Question */}
            <div className="mt-4">
              <label className="text-[#002B53] font-bold">Select Security Question:</label>
              <select
                value={securityQuestion}
                onChange={(e) => setSecurityQuestion(e.target.value)}
                className="w-full mt-1 p-2 rounded border border-gray-300"
                style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
              >
                <option>Select</option>
                <option>Your Date of Birth</option>
                <option>Your Nick Name</option>
                <option>Your Favorite Book</option>
              </select>
            </div>

            {/* Security Answer */}
            <div className="mt-4">
              <label className="text-[#002B53] font-bold">Security Answer:</label>
              <input
                type="text"
                value={securityAnswer}
                onChange={(e) => setSecurityAnswer(e.target.value)}
                className="w-full mt-1 p-2 rounded border border-gray-300"
                style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
              />
            </div>

            {/* New Password */}
            <div className="mt-4">
              <label className="text-[#002B53] font-bold">New Password:</label>
              <input
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                className="w-full mt-1 p-2 rounded border border-gray-300"
                style={{ fontFamily: 'Times New Roman', fontSize: '15px', fontWeight: 'bold' }}
              />
            </div>

            {/* Reset Password Button */}
            <div className="mt-6">
              <button
                onClick={handleResetPassword}
                className="w-full bg-[#002B53] text-white font-bold py-2 rounded hover:bg-[#007ACC]"
              >
                Reset Password
              </button>
            </div>

            {/* Close Modal Button */}
            <div className="mt-4 text-center">
              <button
                onClick={() => setShowForgetModal(false)}
                className="text-[#002B53] font-bold hover:text-red-500"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Login;
