import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import Webcam from 'react-webcam';
import './App.css';
import Student from './components/Student';
import Login from './components/Login';
import Register from './components/Register';
import Attendance from './components/Attendance';
import Dashboard from './components/Dashboard';

function MainPage() {
  const navigate = useNavigate();
  const webcamRef = useRef(null);
  const [time, setTime] = useState(new Date().toLocaleTimeString());
  const [isCameraOn, setIsCameraOn] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => {
      setTime(new Date().toLocaleTimeString());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const handleStudentPanel = () => {
    navigate('/student');
  };

  const handleFaceDetector = async () => {
    if (!isCameraOn) {
      setIsCameraOn(true);
      return;
    }

    // Capture a screenshot from the webcam
    const imageSrc = webcamRef.current.getScreenshot();
    if (!imageSrc) {
      alert("Failed to capture image from webcam.");
      setIsCameraOn(false);
      return;
    }

    // Convert base64 to blob
    const byteString = atob(imageSrc.split(',')[1]);
    const mimeString = imageSrc.split(',')[0].split(':')[1].split(';')[0];
    const ab = new ArrayBuffer(byteString.length);
    const ia = new Uint8Array(ab);
    for (let i = 0; i < byteString.length; i++) {
      ia[i] = byteString.charCodeAt(i);
    }
    const blob = new Blob([ab], { type: mimeString });

    // Send the image to the backend for face recognition
    const formData = new FormData();
    formData.append('image', blob, 'webcam.jpg');

    try {
      const response = await fetch('http://localhost:5000/api/recognize-face', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();

      if (data.status === 200) {
        const student = data.student;
        // Mark attendance
        const attendanceResponse = await fetch('http://localhost:5000/api/mark-attendance', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            id: student.id,
            name: student.name,
            roll: student.roll,
            department: student.department,
          }),
        });
        const attendanceData = await attendanceResponse.json();
        alert(attendanceData.message);
      } else {
        alert(data.message);
      }
    } catch (error) {
      alert(`Error: ${error.message}`);
    }

    setIsCameraOn(false);
  };

  const handleAttendance = () => {
    navigate('/attendance');
  };

  const handleHelpSupport = () => {
    navigate('/dashboard'); // Placeholder for Help Support
  };

  const handleDataTrain = () => {
    alert('Starting Data Training...');
  };

  const handlePhotos = () => {
    alert('Opening Photos...');
  };

  const handleDevelopers = () => {
    navigate('/dashboard'); // Placeholder for Developers
  };

  const handleExit = () => {
    if (window.confirm('Are you sure you want to exit?')) {
      window.close();
    }
  };

  const handleChatBot = () => {
    alert('Opening ChatBot...');
  };

  const handleBackHome = () => {
    setIsCameraOn(false);
    navigate('/');
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="bg-blue-900 text-white flex items-center justify-between p-2">
        <div className="bg-black text-white px-4 py-2">{time}</div>
        <h1 className="text-3xl font-bold text-center flex-1">
          Attendance Management System Using Facial Recognition
        </h1>
      </div>

      <div className="relative p-8">
        <div className="grid grid-cols-4 gap-8 max-w-6xl mx-auto">
          <div className="flex flex-col items-center">
            <button onClick={handleStudentPanel} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/std1.jpg" alt="Student Panel" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleStudentPanel} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Student Panel
            </button>
          </div>

          <div className="flex flex-col items-center">
            <button onClick={handleBackHome} className="mb-2 w-56 bg-gray-500 text-white font-bold py-2">
              Back Home
            </button>
            <button onClick={handleFaceDetector} className="w-56 h-56 bg-gray-500 flex items-center justify-center">
              <img src="/images/det1.jpg" alt="Face Detector" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleFaceDetector} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Face Detector
            </button>
            {isCameraOn && (
              <div className="mt-4 w-96 h-72 bg-black flex items-center justify-center">
                <Webcam
                  audio={false}
                  ref={webcamRef}
                  screenshotFormat="image/jpeg"
                  width={640}
                  height={480}
                  className="object-cover"
                />
              </div>
            )}
          </div>

          <div className="flex flex-col items-center">
            <button onClick={handleAttendance} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/att.jpg" alt="Attendance" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleAttendance} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Attendance
            </button>
          </div>

          <div className="flex flex-col items-center">
            <button onClick={handleHelpSupport} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/hlp.jpg" alt="Help Support" className="w-full h-full object-cover" />
            </button>
            <button onClick={handleHelpSupport} className="mt-2 w-56 bg-white text-blue-900 font-bold py-2">
              Help Support
            </button>
          </div>

          <div className="flex flex-col items-center">
            <button onClick={handleDataTrain} className="w-56 h-56 bg-gray-300 flex items-center justify-center">
              <img src="/images/tra1.jpg" alt="Data Train" className="w-full h-full object-cover" />
            </button>
