import React, { useState, useEffect } from 'react';

function Student({ onBack }) {
  const [formData, setFormData] = useState({
    student_id: '',
    name: '',
    department: 'Select Department',
    course: 'Select Course',
    year: 'Select Year',
    semester: 'Select Semester',
    division: 'A',
    gender: 'Male',
    dob: '',
    phone: '',
    address: '',
    roll_no: '',
    email: '',
    teacher: '',
    photo_sample: 'No',
  });

  const [students, setStudents] = useState([]);
  const [searchRollNo, setSearchRollNo] = useState('');

  // Fetch students on component mount
  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    const response = await fetch('http://localhost:5000/api/students');
    const data = await response.json();
    if (data.status === 200) {
      setStudents(data.students);
    } else {
      alert(data.message);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleAdd = async () => {
    const requiredFields = [
      'student_id', 'name', 'department', 'course', 'year', 'semester',
      'division', 'gender', 'dob', 'phone', 'address', 'roll_no', 'email', 'teacher',
    ];
    if (requiredFields.some(field => !formData[field] || formData[field].includes('Select'))) {
      alert('Please fill all fields!');
      return;
    }

    const response = await fetch('http://localhost:5000/api/students', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    });
    const data = await response.json();
    alert(data.message);
    if (data.status === 200) {
      fetchStudents();
      handleReset();
    }
  };

  const handleUpdate = async () => {
    if (!formData.student_id) {
      alert('Please select a student to update!');
      return;
    }

    const response = await fetch(`http://localhost:5000/api/students/${formData.student_id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    });
    const data = await response.json();
    alert(data.message);
    if (data.status === 200) {
      fetchStudents();
      handleReset();
    }
  };

  const handleDelete = async () => {
    if (!formData.student_id) {
      alert('Please select a student to delete!');
      return;
    }

    if (!window.confirm('Do you want to delete this student?')) return;

    const response = await fetch(`http://localhost:5000/api/students/${formData.student_id}`, {
      method: 'DELETE',
    });
    const data = await response.json();
    alert(data.message);
    if (data.status === 200) {
      fetchStudents();
      handleReset();
    }
  };

  const handleReset = () => {
    setFormData({
      student_id: '',
      name: '',
      department: 'Select Department',
      course: 'Select Course',
      year: 'Select Year',
      semester: 'Select Semester',
      division: 'A',
      gender: 'Male',
      dob: '',
      phone: '',
      address: '',
      roll_no: '',
      email: '',
      teacher: '',
      photo_sample: 'No',
    });
  };

  const handleSearch = async () => {
    if (!searchRollNo) {
      alert('Please enter a roll number to search!');
      return;
    }

    const response = await fetch(`http://localhost:5000/api/students/search?roll_no=${searchRollNo}`);
    const data = await response.json();
    if (data.status === 200) {
      setStudents(data.students);
    } else {
      alert(data.message);
    }
  };

  const handleRowClick = (student) => {
    setFormData(student);
  };

  const handleTakePhoto = () => {
    alert('Photo capture is not implemented in the browser. Consider using a library like react-webcam for camera access.');
  };

  const handleDeletePhoto = () => {
    alert('Photo deletion is not implemented in the browser. This requires backend file system access.');
  };

  return (
    <div
      className="min-h-screen p-8 relative"
      style={{
        backgroundImage: 'url(/images/bb.jpg)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
      }}
    >
      {/* Overlay to improve text readability */}
      <div className="absolute inset-0 bg-black bg-opacity-50"></div>

      {/* Content */}
      <div className="relative z-10">
        {/* Header */}
        <div className="bg-blue-900 text-white p-4 flex justify-between items-center">
          <button onClick={onBack} className="bg-gray-500 text-white px-4 py-2 rounded">
            Back Home
          </button>
          <h1 className="text-3xl font-bold">Welcome to Student Panel</h1>
          <div></div>
        </div>

        <div className="bg-white p-6 rounded shadow mt-4">
          <div className="flex">
            {/* Left Section: Form */}
            <div className="w-1/2 pr-4 border-r">
              <h2 className="text-xl font-bold text-blue-900 mb-4">Student Details</h2>

              {/* Current Course */}
              <div className="border p-4 mb-4">
                <h3 className="text-lg font-bold text-blue-900 mb-2">Current Course</h3>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-blue-900 font-bold">Department</label>
                    <select
                      name="department"
                      value={formData.department}
                      onChange={handleInputChange}
                      className="w-full p-2 border rounded"
                    >
                      <option>Select Department</option>
                      <option>Computer</option>
                      <option>IT</option>
                      <option>Civil</option>
                      <option>Mechanical</option>
                      <option>Electrical</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-blue-900 font-bold">Course</label>
                    <select
                      name="course"
                      value={formData.course}
                      onChange={handleInputChange}
                      className="w-full p-2 border rounded"
                    >
                      <option>Select Course</option>
                      <option>SE</option>
                      <option>FE</option>
                      <option>TE</option>
                      <option>BE</option>
                      <option>MS</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-blue-900 font-bold">Year</label>
                    <select
                      name="year"
                      value={formData.year}
                      onChange={handleInputChange}
                      className="w-full p-2 border rounded"
                    >
                      <option>Select Year</option>
                      <option>2017-21</option>
                      <option>2018-22</option>
                      <option>2019-23</option>
                      <option>2020-24</option>
                      <option>2021-25</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-blue-900 font-bold">Semester</label>
                    <select
                      name="semester"
                      value={formData.semester}
                      onChange={handleInputChange}
                      className="w-full p-2 border rounded"
                    >
                      <option>Select Semester</option>
                      <option>Semester-1</option>
                      <option>Semester-2</option>
                      <option>Semester-3</option>
                      <option>Semester-4</option>
                      <option>Semester-5</option>
                      <option>Semester-6</option>
                      <option>Semester-7</option>
                      <option>Semester-8</option>
                    </select>
                  </div>
                </div>
              </div>

              {/* Class Student Information */}
              <div className="border p-4">
                <h3 className="text-lg font-bold text-blue-900 mb-2">Class Student Information</h3>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-blue-900 font-bold">Student ID</label>
                    <input
                      type="text"
                      name="student_id"
                      value={formData.student_id}
                      onChange={handleInputChange}
                      className="w-full p-2 border rounded"
                    />
                  </div>
                  <div>
                    <label className="block text-blue-900 font-bold">Student Name</label>
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      className="w-full p-2 border rounded"
                    />
                  </div>
                  <div>
                    <label className="block text-blue-900 font-bold">Class Division</label>
                    <select
                      name="division"
                      value={formData.division}
                      onChange={handleInputChange}
                      className="w-full p-2 border rounded"
                    >
                      <option>A</option>
                      <option>B</option>
                      <option>C</option>
                      <option>D</option>
                      <option>E</option>
                      <option>F</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-blue-900 font-bold">Roll No</label>
                    <input
                      type="text"
                      name="roll_no"
                      value={formData.roll_no}
                      onChange={handleInputChange}
                      className="w-full p-2 border rounded"
                    />
                  </div>
                  <div>
                    <label className="block text-blue-900 font-bold">Gender</label>
                    <select
                      name="gender"
                      value={formData.gender}
                      onChange={handleInputChange}
                      className="w-full p-2 border rounded"
                    >
                      <option>Male</option>
                      <option>Female</option>
                      <option>Others</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-blue-900 font-bold">DOB</label>
                    <input
                      type="text"
                      name="dob"
                      value={formData.dob}
                      onChange={handleInputChange}
                      className="w-full p-2 border rounded"
                      placeholder="YYYY-MM-DD"
                    />
                  </div>
                  <div>
                    <label className="block text-blue-900 font-bold">Email</label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      className="w-full p-2 border rounded"
                    />
                  </div>
                  <div>
                    <label className="block text-blue-900 font-bold">Phone</label>
                    <input
                      type="text"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                      className="w-full p-2 border rounded"
                    />
                  </div>
                  <div>
                    <label className="block text-blue-900 font-bold">Address</label>
                    <input
                      type="text"
                      name="address"
                      value={formData.address}
                      onChange={handleInputChange}
                      className="w-full p-2 border rounded"
                    />
                  </div>
                  <div>
                    <label className="block text-blue-900 font-bold">Teacher Name</label>
                    <input
                      type="text"
                      name="teacher"
                      value={formData.teacher}
                      onChange={handleInputChange}
                      className="w-full p-2 border rounded"
                    />
                  </div>
                </div>

                {/* Radio Buttons for Photo Sample */}
                <div className="mt-4 flex space-x-4">
                  <label className="flex items-center">
                    <input
                      type="radio"
                      name="photo_sample"
                      value="Yes"
                      checked={formData.photo_sample === 'Yes'}
                      onChange={handleInputChange}
                      className="mr-2"
                    />
                    Take Photo Sample
                  </label>
                  <label className="flex items-center">
                    <input
                      type="radio"
                      name="photo_sample"
                      value="No"
                      checked={formData.photo_sample === 'No'}
                      onChange={handleInputChange}
                      className="mr-2"
                    />
                    No Photo Sample
                  </label>
                </div>
              </div>

              {/* Buttons */}
              <div className="mt-4 flex space-x-2">
                <button onClick={handleAdd} className="bg-blue-900 text-white px-4 py-2 rounded">
                  Save
                </button>
                <button onClick={handleUpdate} className="bg-blue-900 text-white px-4 py-2 rounded">
                  Update
                </button>
                <button onClick={handleDelete} className="bg-blue-900 text-white px-4 py-2 rounded">
                  Delete
                </button>
                <button onClick={handleReset} className="bg-blue-900 text-white px-4 py-2 rounded">
                  Reset
                </button>
                {formData.photo_sample === 'Yes' && (
                  <>
                    <button onClick={handleTakePhoto} className="bg-blue-900 text-white px-4 py-2 rounded">
                      Take Pic
                    </button>
                    <button onClick={handleDeletePhoto} className="bg-blue-900 text-white px-4 py-2 rounded">
                      Delete Pic
                    </button>
                  </>
                )}
              </div>
            </div>

            {/* Right Section: Table */}
            <div className="w-1/2 pl-4">
              <h2 className="text-xl font-bold text-blue-900 mb-4">Student Details</h2>

              {/* Search Bar */}
              <div className="border p-4 mb-4">
                <h3 className="text-lg font-bold text-blue-900 mb-2">Search System</h3>
                <div className="flex space-x-2">
                  <select className="p-2 border rounded">
                    <option>Roll-No</option>
                  </select>
                  <input
                    type="text"
                    value={searchRollNo}
                    onChange={(e) => setSearchRollNo(e.target.value)}
                    className="p-2 border rounded"
                    placeholder="Enter Roll No"
                  />
                  <button onClick={handleSearch} className="bg-blue-900 text-white px-4 py-2 rounded">
                    Search
                  </button>
                  <button onClick={fetchStudents} className="bg-blue-900 text-white px-4 py-2 rounded">
                    Show All
                  </button>
                </div>
              </div>

              {/* Table */}
              <div className="border p-4 max-h-96 overflow-auto">
                <table className="w-full border-collapse">
                  <thead>
                    <tr className="bg-blue-900 text-white">
                      <th className="border p-2">ID</th>
                      <th className="border p-2">Name</th>
                      <th className="border p-2">Dep</th>
                      <th className="border p-2">Course</th>
                      <th className="border p-2">Year</th>
                      <th className="border p-2">Sem</th>
                      <th className="border p-2">Div</th>
                      <th className="border p-2">Gender</th>
                      <th className="border p-2">DOB</th>
                      <th className="border p-2">Phone</th>
                      <th className="border p-2">Address</th>
                      <th className="border p-2">Roll-No</th>
                      <th className="border p-2">Email</th>
                      <th className="border p-2">Teacher</th>
                      <th className="border p-2">Photo</th>
                    </tr>
                  </thead>
                  <tbody>
                    {students.map((student) => (
                      <tr
                        key={student.student_id}
                        onClick={() => handleRowClick(student)}
                        className="cursor-pointer hover:bg-gray-100"
                      >
                        <td className="border p-2">{student.student_id}</td>
                        <td className="border p-2">{student.name}</td>
                        <td className="border p-2">{student.department}</td>
                        <td className="border p-2">{student.course}</td>
                        <td className="border p-2">{student.year}</td>
                        <td className="border p-2">{student.semester}</td>
                        <td className="border p-2">{student.division}</td>
                        <td className="border p-2">{student.gender}</td>
                        <td className="border p-2">{student.dob}</td>
                        <td className="border p-2">{student.phone}</td>
                        <td className="border p-2">{student.address}</td>
                        <td className="border p-2">{student.roll_no}</td>
                        <td className="border p-2">{student.email}</td>
                        <td className="border p-2">{student.teacher}</td>
                        <td className="border p-2">{student.photo_sample}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Student;
